405         private static void OutputXMLToFile(string XML)
406         {
407             string formattedXML = "";
408             using (var stringWriter = new StringWriter())
409             using (var xmlTextWriter = XmlWriter.Create(stringWriter))
410             {
411                 string xmlOut = XML;
412                 formattedXML = System.Xml.Linq.XDocument.Parse(xmlOut).ToString();
413             }
414 
415             if (File.Exists("XMLOutput_" + SPPUBOrderNumber + ".txt"))
416             {
417                 File.Delete("XMLOutput_" + SPPUBOrderNumber + ".txt");
418             }
419             using (System.IO.StreamWriter file = new System.IO.StreamWriter(@"XMLOutput_" + SPPUBOrderNumber + ".txt", true))
420             {
421                 file.WriteLine(formattedXML);
422             }
423         }
