17         public static void CallConfigureOne(string key, string payload, string url)
18         {
19             string logEvent = "CALLING C1 WEBSERVICE";
20             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
21             string sURL = url;
22             HttpWebRequest objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
23             objRequest.Method = "POST";
24             objRequest.ContentType = "text/xml";
25             objRequest.Headers.Add("SOAPAction", key);
26             objRequest.Timeout = 120000;        //increased
27             objRequest.ReadWriteTimeout = 120000;   //increased
28             string xmlPayload = payload;
29             StringBuilder data = new StringBuilder();
30             data.Append(xmlPayload);
31             byte[] byteData = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
32             objRequest.ContentLength = byteData.Length;
33             
34             using (Stream postStream = objRequest.GetRequestStream())
35             {
36                 postStream.Write(byteData, 0, byteData.Length);
37             }
38             XmlDocument xmlResult = new XmlDocument();
39             string result = "";
40 
41             try
42             {
43                 //return response from AXIS (if any)
44                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
45                 {
46                     StreamReader reader = new StreamReader(response.GetResponseStream());
47                     result = reader.ReadToEnd();
48                     reader.Close();
49                 }
50                 try
51                 {
52                     xmlResult.LoadXml(result);
53                 }
54                 catch (Exception ex2)
55                 {
56                     logEvent = "ERROR LOADING XML FROM WEB SERVICE: " + ex2.Message;
57                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
58                     return;
59                 }
60                 if (Triggers.caller == "ORDER") {StagingUtilities.MapXMLToSQL(xmlResult);}
61                                 
62                 Triggers.caller = "";
63                 using (var stringWriter = new StringWriter())
64                 using (var xmlTextWriter = XmlWriter.Create(stringWriter))
65                 {
66                     xmlResult.WriteTo(xmlTextWriter);
67                     xmlTextWriter.Flush();
68                     string xmlOut = stringWriter.GetStringBuilder().ToString();
69                     Triggers.wsReturn = System.Xml.Linq.XDocument.Parse(xmlOut).ToString();
70                 }
71             }
72             catch (WebException wex1)
73             {
74                 logEvent = "ERROR RETURNED FROM C1 WEBSERVICE: " + wex1.Message + " : " + wex1.Response.ToString();
75                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
76                 return;
77             }
78 
79             DatabaseFactory.CfgImport(Triggers.pubOrderNumber);                 //map staging-table data into Syteline
80             
81             //Retrieve the SL order# (if not found, default to using the C1 order#):
82             string SPOrderNumber = string.IsNullOrEmpty(DatabaseFactory.RetrieveSLCO(Triggers.pubOrderNumber)) ? Triggers.pubOrderNumber : DatabaseFactory.RetrieveSLCO(Triggers.pubOrderNumber);
83             SPPUBOrderNumber = SPOrderNumber;
84             OutputXMLToFile(Triggers.wsReturn);
85 
86             try
87             {
88                 logEvent = "Retrieving/Saving Document Files For Order: " + Triggers.pubOrderNumber + " -> " + SPOrderNumber;
89                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
90 
91                 //Retrieve documents for order, reusing xmlResult above
92                 string documentType = "Design";
93                 string documentName = "";
94                 string orderNumber = Triggers.pubOrderNumber;
95                 string[] docs;
96                 docs = new string[50];
97                 int arrayindex = 0;
98                 
99                 //Retrieve the serialNumber from the XML 
100                 string configSerial = "";
101                 XmlNodeList xnlSN = xmlResult.GetElementsByTagName("serialNumber");
102                 foreach (XmlNode node in xnlSN)
103                 {
104                     configSerial = node.InnerText;
105                 }
106 
107                 string useMethod = "";
108                 key = "getDocument";
109                 useMethod = C1Dictionaries.webmethods[key];
110                 string documentSerialNumber = configSerial;
111                 arrayindex = 0;
112 
113                 XmlNodeList xnl = xmlResult.GetElementsByTagName("fileName");
114                 foreach (XmlNode node in xnl)
115                 {
116                     docs[arrayindex] = node.InnerText;
117                     arrayindex += 1;
118                 }
119 
120                 logEvent = "DEBUG: Finished building document filename array";
121                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
122 
123                 //We now have an array of document filenames; process by retrieving and saving each file
124                 int upperBound = arrayindex;
125                 arrayindex = 0;
126                 string documentFilesSaved = "";
127                 documentSerialNumber = configSerial;
128                 string SharepointLocation = DatabaseFactory.splocation;
129                 NetworkShare.DisconnectFromShare(SharepointLocation, true);
130                 NetworkShare.ConnectToShare(SharepointLocation, DatabaseFactory.spuname, DatabaseFactory.sppassword);
131 
132                 logEvent = "DEBUG: SP Share credential fixed";
133                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
134 
135                 //Create Sharepoint folder for order#
136                 string SharepointCopyLocation = SharepointLocation + SPOrderNumber + "\\";
137                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation); }
138                 catch (Exception edi)
139                 {
140                     //directory already exists, nothing further needs done
141                 }
142                 
143                 while (arrayindex < upperBound)
144                 {
145                     documentName = docs[arrayindex];
146                     xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soapenv:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:ws=" + (char)34 + "http://ws.configureone.com" + (char)34 + " xmlns:mod=" + (char)34 + "http://model.ws.configureone.com" + (char)34 + " xmlns:soapenv=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soapenv:Body><ws:" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><ws:document><mod:type>" + documentType + "</mod:type><mod:serialNumber>" + documentSerialNumber + "</mod:serialNumber><mod:files><mod:fileName>" + documentName + "</mod:fileName><mod:content>" + documentName + "</mod:content></mod:files></ws:document></ws:" + key + "></soapenv:Body></soapenv:Envelope>";
147                     sURL = useMethod;
148                     objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
149                     objRequest.Method = "POST";
150                     objRequest.ContentType = "text/xml";
151                     objRequest.Headers.Add("SOAPAction", key);
152                     objRequest.Timeout = 120000;
153                     objRequest.ReadWriteTimeout = 120000;
154                     objRequest.KeepAlive = true;
155 
156                     logEvent = "DEBUG: Requested document from C1";
157                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
158 
159                     data = new StringBuilder();
160                     data.Append(xmlPayload);
161                     byte[] byteDataDoc = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
162                     objRequest.ContentLength = byteDataDoc.Length;
163 
164                     using (Stream postStream = objRequest.GetRequestStream())
165                     {
166                         postStream.ReadTimeout = 120000;
167                         postStream.WriteTimeout = 120000;
168                         postStream.Write(byteDataDoc, 0, byteDataDoc.Length);
169                     }
170                     xmlResult = new XmlDocument();
171                     result = "";
172 
173                     logEvent = "DEBUG: Waiting for response from C1";
174                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
175 
176                     // using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
177                     try
178                     {
179                         using (HttpWebResponse response = (HttpWebResponse)objRequest.GetResponse())
180                         {
181                             StreamReader reader = new StreamReader(response.GetResponseStream());
182                             result = reader.ReadToEnd();
183                             reader.Close();
184 
185                             try
186                             {
187                                 xmlResult.LoadXml(result);
188                             }
189                             catch (Exception ex1)
190                             {
191                                 return;
192                             }
193 
194                             logEvent = "DEBUG: Response received from C1";
195                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
196 
197                             XmlNodeList xnldoc = xmlResult.GetElementsByTagName("content");
198                             foreach (XmlNode node in xnldoc)
199                             {
200                                 logEvent = "DEBUG: Copying file to Sharepoint: " + SPOrderNumber + "_" + docs[arrayindex];
201                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
202 
203                                 try
204                                 {
205                                     byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
206                                     string fileToCopy = @"C:\C1TEMP\" + SPOrderNumber + "_" + docs[arrayindex];
207 
208                                     //deboog
209                                     File.WriteAllBytes(SharepointCopyLocation + SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
210                                     //end deboog
211 
212                                     //File.WriteAllBytes(fileToCopy, pdfByteArray);
213                                     //File.Copy(fileToCopy, SharepointCopyLocation + SPOrderNumber + "_" + docs[arrayindex], true);
214                                     documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
215                                     break;      //deboog
216                                 }
217                                 catch (Exception ts1)
218                                 {
219                                     logEvent = "An error occurred copying file to Sharepoint: " + SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS1 Exception";
220                                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
221                                     SendMail.MailMessage(logEvent, "TIMEOUT");
222                                 }
223                             }
224                         }
225                     }
226                     catch (Exception ts2)
227                     {
228                         logEvent = "An error occurred copying file to Sharepoint: " + SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS2 Exception";
229                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
230                         SendMail.MailMessage(logEvent, "TIMEOUT");
231                     }
232                     arrayindex += 1;
233                 }
234 
235                 //Save XML output to SharePoint as well
236                 File.Copy("XMLOutput_" + SPPUBOrderNumber + ".txt", SharepointCopyLocation + "XMLOutput_" + SPPUBOrderNumber + ".txt");
237 
238                 logEvent = "Retrieved/Saved Following Document Files: " + Environment.NewLine + Environment.NewLine + documentFilesSaved;
239                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
240                                 
241                 //Update order-status and ref number in C1 to Ordered and SL order#, respectively
242                 key = "updateOrder";
243                 useMethod = C1Dictionaries.webmethods[key];
244                 string C1orderNumber = orderNumber;
245                 string C1orderReference = SPOrderNumber;
246                 string C1orderStatus = "OR";              //Ordered
247                 xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soap:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:soap=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soap:Body><" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><param1>" + orderNumber + "</param1><param2>" + C1orderReference + "</param2><param3>" + C1orderStatus + "</param3></" + key + "></soap:Body></soap:Envelope>";
248                 sURL = useMethod;
249                 pubURL = sURL;
250                 pubPayload = xmlPayload;
251                 logEvent = "XML Payload To AXIS: " + xmlPayload;
252                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
253                 objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
254                 objRequest.Method = "POST";
255                 objRequest.ContentType = "text/xml";
256                 objRequest.Headers.Add("SOAPAction", key);
257                 objRequest.Timeout = 120000;
258                 objRequest.ReadWriteTimeout = 120000;
259 
260                 data = new StringBuilder();
261                 data.Append(xmlPayload);
262                 byte[] byteDataStatus = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
263                 objRequest.ContentLength = byteDataStatus.Length;
264                 using (Stream postStream = objRequest.GetRequestStream())
265                 {
266                     postStream.ReadTimeout = 120000;
267                     postStream.WriteTimeout = 120000;
268                     postStream.Write(byteDataStatus, 0, byteDataStatus.Length);
269                 }
270                 
271                 //return response from AXIS (if any)
272                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
273                 {
274                     StreamReader reader = new StreamReader(response.GetResponseStream());
275                     result = reader.ReadToEnd();
276                     reader.Close();
277                 }
278                 
279                 try
280                 {
281                     xmlResult.LoadXml(result);
282                     xnl = xmlResult.GetElementsByTagName("success");
283                     foreach (XmlNode node in xnl)
284                     {
285                         switch (node.InnerText)
286                         {
287                             case "true":
288                                 logEvent = "Status-Set Result: Success";
289                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
290                                 break;
291                             default:
292                                 logEvent = "Status-Set Result: Failure";
293                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
294                                 break;
295                         }
296                     }
297                 }
298                 catch (Exception ex2)
299                 {
300                     logEvent = "ERROR LOADING XML FROM WEB SERVICE ON C1 ORDER STATUS SET: " + ex2.Message;
301                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
302                     return;
303                 }
304                 
305                 logEvent = "C1 Order Status And Reference Completed For C1 Order: " + orderNumber + " - Syteline Order: " + SPOrderNumber;
306                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
307             }
308             catch (Exception exd)
309             {
310                 logEvent = "ERROR: " + exd.Message + " -> " + exd.Source;
311                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
312             }
313         }
314         private static void OutputXMLToFile(string XML)
315         {
316             string formattedXML = "";
317             using (var stringWriter = new StringWriter())
318             using (var xmlTextWriter = XmlWriter.Create(stringWriter))
319             {
320                 string xmlOut = XML;
321                 formattedXML = System.Xml.Linq.XDocument.Parse(xmlOut).ToString();
322             }
323 
324             if (File.Exists("XMLOutput_" + SPPUBOrderNumber + ".txt"))
325             {
326                 File.Delete("XMLOutput_" + SPPUBOrderNumber + ".txt");
327             }
328             using (System.IO.StreamWriter file = new System.IO.StreamWriter(@"XMLOutput_" + SPPUBOrderNumber + ".txt", true))
329             {
330                 file.WriteLine(formattedXML);
331             }
332         }
