18         public static void CallConfigureOne(string key, string payload, string url)
19         {
20             string logEvent = "CALLING C1 WEBSERVICE";
21             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
22             string sURL = url;
23             HttpWebRequest objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
24             objRequest.Method = "POST";
25             objRequest.ContentType = "text/xml";
26             objRequest.Headers.Add("SOAPAction", key);
27             string xmlPayload = payload;
28             StringBuilder data = new StringBuilder();
29             data.Append(xmlPayload);
30             byte[] byteData = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
31             objRequest.ContentLength = byteData.Length;
32             
33             using (Stream postStream = objRequest.GetRequestStream())
34             {
35                 postStream.Write(byteData, 0, byteData.Length);
36             }
37             XmlDocument xmlResult = new XmlDocument();
38             string result = "";
39 
40             try
41             {
42                 //return response from AXIS (if any)
43                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
44                 {
45                     StreamReader reader = new StreamReader(response.GetResponseStream());
46                     result = reader.ReadToEnd();
47                     reader.Close();
48                 }
49                 try
50                 {
51                     xmlResult.LoadXml(result);
52                 }
53                 catch (Exception ex2)
54                 {
55                     logEvent = "ERROR LOADING XML FROM WEB SERVICE: " + ex2.Message;
56                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
57                     return;
58                 }
59                 if (Triggers.caller == "ORDER") {StagingUtilities.MapXMLToSQL(xmlResult);}
60                                 
61                 Triggers.caller = "";
62                 using (var stringWriter = new StringWriter())
63                 using (var xmlTextWriter = XmlWriter.Create(stringWriter))
64                 {
65                     xmlResult.WriteTo(xmlTextWriter);
66                     xmlTextWriter.Flush();
67                     string xmlOut = stringWriter.GetStringBuilder().ToString();
68                     Triggers.wsReturn = System.Xml.Linq.XDocument.Parse(xmlOut).ToString();
69                 }
70             }
71             catch (WebException wex1)
72             {
73                 logEvent = "ERROR RETURNED FROM C1 WEBSERVICE: " + wex1.Message + " : " + wex1.Response.ToString();
74                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
75                 return;
76             }
77                         
78             DatabaseFactory.CfgImport(Triggers.pubOrderNumber);
79             
80             //Now retrieve the SL order# (if not found, default to using the C1 order#):
81             string SPOrderNumber = string.IsNullOrEmpty(DatabaseFactory.RetrieveSLCO(Triggers.pubOrderNumber)) ? Triggers.pubOrderNumber : DatabaseFactory.RetrieveSLCO(Triggers.pubOrderNumber);
82             SPPUBOrderNumber = SPOrderNumber;
83 
84             try
85             {
86                 logEvent = "Retrieving/Saving Document Files For Order: " + Triggers.pubOrderNumber + " -> " + SPOrderNumber;
87                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
88 
89                 //Retrieve documents for order, reusing xmlResult above
90                 string documentType = "Design";
91                 string documentName = "";
92                 string orderNumber = Triggers.pubOrderNumber;
93                 string[] docs;
94                 docs = new string[50];
95                 int arrayindex = 0;
96                 
97                 //Retrieve the serialNumber from the XML 
98                 string configSerial = "";
99                 XmlNodeList xnlSN = xmlResult.GetElementsByTagName("serialNumber");
100                 foreach (XmlNode node in xnlSN)
101                 {
102                     configSerial = node.InnerText;
103                 }
104 
105                 string useMethod = "http://nationaldev.conceptconfigurator.com/webservices/services/ConceptAccess?method=getDocument";
106                 key = "getDocument";
107                 string documentSerialNumber = configSerial;
108                 arrayindex = 0;
109 
110                 XmlNodeList xnl = xmlResult.GetElementsByTagName("fileName");
111                 foreach (XmlNode node in xnl)
112                 {
113                     docs[arrayindex] = node.InnerText;
114                     arrayindex += 1;
115                 }
116                 
117                 //We now have an array of document filenames; process by retrieving and saving each file
118                 int upperBound = arrayindex;
119                 arrayindex = 0;
120                 string documentFilesSaved = "";
121                 documentSerialNumber = configSerial;
122                 string SharepointLocation = DatabaseFactory.splocation;
123                 NetworkShare.DisconnectFromShare(SharepointLocation, true);
124                 NetworkShare.ConnectToShare(SharepointLocation, DatabaseFactory.spuname, DatabaseFactory.sppassword);
125 
126                 //Create Sharepoint folder for order#
127                 string SharepointCopyLocation = SharepointLocation + SPOrderNumber + "\\";
128                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation); }
129                 catch (Exception edi)
130                 {
131                     //directory already exists, nothing further needs done
132                 }
133                 
134                 while (arrayindex < upperBound)
135                 {
136                     documentName = docs[arrayindex];
137                     xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soapenv:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:ws=" + (char)34 + "http://ws.configureone.com" + (char)34 + " xmlns:mod=" + (char)34 + "http://model.ws.configureone.com" + (char)34 + " xmlns:soapenv=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soapenv:Body><ws:" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><ws:document><mod:type>" + documentType + "</mod:type><mod:serialNumber>" + documentSerialNumber + "</mod:serialNumber><mod:files><mod:fileName>" + documentName + "</mod:fileName><mod:content>" + documentName + "</mod:content></mod:files></ws:document></ws:" + key + "></soapenv:Body></soapenv:Envelope>";
138                     sURL = useMethod;
139                     objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
140                     objRequest.Method = "POST";
141                     objRequest.ContentType = "text/xml";
142                     objRequest.Headers.Add("SOAPAction", key);
143 
144                     data = new StringBuilder();
145                     data.Append(xmlPayload);
146                     byte[] byteDataDoc = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
147                     objRequest.ContentLength = byteDataDoc.Length;
148 
149                     using (Stream postStream = objRequest.GetRequestStream())
150                     {
151                         postStream.Write(byteDataDoc, 0, byteDataDoc.Length);
152                     }
153                     xmlResult = new XmlDocument();
154                     result = "";
155                     using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
156                     {
157                         StreamReader reader = new StreamReader(response.GetResponseStream());
158                         result = reader.ReadToEnd();
159                         reader.Close();
160 
161                         try
162                         {
163                             xmlResult.LoadXml(result);
164                         }
165                         catch (Exception ex1)
166                         {
167                             return;
168                         }
169                         
170                         XmlNodeList xnldoc = xmlResult.GetElementsByTagName("content");
171                         foreach (XmlNode node in xnldoc)
172                         {
173                             byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
174                             string fileToCopy = @"C:\C1TEMP\" + SPOrderNumber + "_" + docs[arrayindex];
175                             File.WriteAllBytes(fileToCopy, pdfByteArray);
176                             File.Copy(fileToCopy, SharepointCopyLocation + SPOrderNumber + "_" + docs[arrayindex], true);
177                             documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
178                         }
179                     }
180                     arrayindex += 1;
181                 }
182 
183                 logEvent = "Retrieved/Saved Following Document Files: " + Environment.NewLine + Environment.NewLine + documentFilesSaved;
184                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
185                                 
186                 //Update order-status and ref number in C1 to Ordered and SL order#, respectively
187                 key = "updateOrder";
188                 useMethod = C1Dictionaries.webmethods[key];
189                 string C1orderNumber = orderNumber;
190                 string C1orderReference = SPOrderNumber;
191                 string C1orderStatus = "OR";              //Ordered
192                 xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soap:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:soap=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soap:Body><" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><param1>" + orderNumber + "</param1><param2>" + C1orderReference + "</param2><param3>" + C1orderStatus + "</param3></" + key + "></soap:Body></soap:Envelope>";
193                 sURL = useMethod;
194                 pubURL = sURL;
195                 pubPayload = xmlPayload;
196                 logEvent = "XML Payload To AXIS: " + xmlPayload;
197                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
198                 objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
199                 objRequest.Method = "POST";
200                 objRequest.ContentType = "text/xml";
201                 objRequest.Headers.Add("SOAPAction", key);
202 
203                 data = new StringBuilder();
204                 data.Append(xmlPayload);
205                 byte[] byteDataStatus = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
206                 objRequest.ContentLength = byteDataStatus.Length;
207                 using (Stream postStream = objRequest.GetRequestStream())
208                 {
209                     postStream.Write(byteDataStatus, 0, byteDataStatus.Length);
210                 }
211 
212                 //return response from AXIS (if any)
213                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
214                 {
215                     StreamReader reader = new StreamReader(response.GetResponseStream());
216                     result = reader.ReadToEnd();
217                     reader.Close();
218                 }
219 
220                 try
221                 {
222                     xmlResult.LoadXml(result);
223                     xnl = xmlResult.GetElementsByTagName("success");
224                     foreach (XmlNode node in xnl)
225                     {
226                         switch (node.InnerText)
227                         {
228                             case "true":
229                                 logEvent = "Status-Set Result: Success";
230                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
231                                 break;
232                             default:
233                                 logEvent = "Status-Set Result: Failure";
234                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
235                                 break;
236                         }
237                     }
238                 }
239                 catch (Exception ex2)
240                 {
241                     logEvent = "ERROR LOADING XML FROM WEB SERVICE ON C1 ORDER STATUS SET: " + ex2.Message;
242                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
243                     return;
244                 }
245                 
246                 logEvent = "C1 Order Status And Reference Completed For C1 Order: " + orderNumber + " - Syteline Order: " + SPOrderNumber;
247                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
248 
249                 //return;
250             }
251             catch (Exception exd)
252             {
253                 logEvent = "ERROR: " + exd.Message + " -> " + exd.Source;
254                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
255             }
256         }
