27         public void SetConnectionString()
28         {
29             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
30             if (reg == null)
31             {
32                 //create keys if they don't already exist and set values
33                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
34                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
35                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
36                 string proddbConnectionData = File.ReadAllText(@"C:\C1\proddbconnection.dat");
37                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
38                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
39                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
40                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
41                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
42                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
43                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
44                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
45                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
46                 //set registry values with defaults
47                 reg.SetValue("PROD_DB", proddbConnectionData);
48                 reg.SetValue("DB", dbConnectionData);
49                 reg.SetValue("ENCKEY", dbkey);
50                 reg.SetValue("EMAILADDR", emailaddr);
51                 reg.SetValue("EMAILSERVER", emailServer);
52                 reg.SetValue("EMAILFROM", emailFrom);
53                 reg.SetValue("SPLOCATION", splocation);
54                 reg.SetValue("SPUNAME", spuname);
55                 reg.SetValue("SPPASSWORD", sppassword);
56                 reg.SetValue("DEBUGLOGGING", "NO");
57                 reg.SetValue("PROTECT", dbprotect);
58                 reg.SetValue("WS_UNAME", ws_uname);
59                 reg.SetValue("WS_PASSWORD", ws_password);
60             }
61             
62             //decrypt cs
63             string sKey = reg.GetValue("ENCKEY").ToString();
64             connectionString = reg.GetValue("DB").ToString();
65             emailaddr = reg.GetValue("EMAILADDR").ToString();
66             emailServer = reg.GetValue("EMAILSERVER").ToString();
67             emailFrom = reg.GetValue("EMAILFROM").ToString();
68             splocation = reg.GetValue("SPLOCATION").ToString();
69             spuname = reg.GetValue("SPUNAME").ToString();
70             sppassword = reg.GetValue("SPPASSWORD").ToString();
71             connectionString = crypto.DecryptCS(connectionString, sKey);
72             spuname = crypto.DecryptCS(spuname, sKey);
73             sppassword = crypto.DecryptCS(sppassword, sKey);
74             dbprotect = reg.GetValue("PROTECT").ToString();
75             ws_uname = reg.GetValue("WS_UNAME").ToString();
76             ws_password = reg.GetValue("WS_PASSWORD").ToString();
77 
78             debugLogging = false;
79             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
80             {
81                 debugLogging = true;
82             }
83             
84             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
85             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
86             {
87                 connectionString = reg.GetValue("PROD_DB").ToString();
88             }
89         }        
