148         public static string UserName(string id)
149         {
150             string name = "N/A";
151             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
152             using (SqlConnection myConnection = new SqlConnection(connectionString))
153             {
154                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
155                 myCommand.CommandTimeout = 120000;
156                 myConnection.Open();
157                 using (SqlDataReader reader = myCommand.ExecuteReader())
158                 {
159                     if (reader.HasRows)
160                     {
161                         if (reader.Read())
162                         {
163                             name = reader["UNAME"].ToString();
164                         }
165                     }
166                     reader.Close();
167                 }
168                 myConnection.Close();
169             }
170             return name;
171         }
172         public static void ResequenceBOM(string orderNumber, int orderLine)
173         {
174             //call SP to resequence BOM records based on parentIDs
175             SqlConnection connection = new SqlConnection(connectionString);
176             SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
177             command.CommandType = CommandType.StoredProcedure;
178             command.Parameters.AddWithValue("@OrderNumber", orderNumber);
179             command.Parameters.AddWithValue("@OrderLine", orderLine);
180             command.CommandTimeout = 120000;
181            
182             connection.Open();
183             command.ExecuteNonQuery();
184             connection.Close();
185         }
186         public static void CfgImport(string orderNumber)
187         {
188             //call SP to import data into Syteline
189             int CreateOrder = 1;
190             
191             SqlConnection connection = new SqlConnection(connectionString);
192             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
193             command.CommandType = CommandType.StoredProcedure;
194             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
195             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
196             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
197             command.CommandTimeout = 120000;
198             connection.Open();
199             try
200             {
201                 command.ExecuteNonQuery();
202             }
203             catch (Exception spI)
204             {
205                 //A timeout has likely occurred; try again
206                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
207                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
208 
209                 connection.Close();
210                 connection.Open();
211                 command.ExecuteNonQuery();
212             }
213             
214             connection.Close();
215         }
