270         public static string RetrieveSLCO (string orderNum)
271         {
272             string SLOrderNumber = "";
273             //SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
274             SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with (nolock) Where c.order_num = " + (char)39 + orderNum + (char)39;
275             using (SqlConnection myConnection = new SqlConnection(connectionString))
276             {
277                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
