27         public void SetConnectionString()
28         {
29             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
30             if (reg == null)
31             {
32                 //create keys if they don't already exist and set values
33                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
34                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
35                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
36                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
37                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
38                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
39                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
40                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
41                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
42                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
43                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
44                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
45                 //set registry values with defaults
46                 reg.SetValue("PROD_DB", @"C:\C1\proddbconnection.dat");
47                 reg.SetValue("DB", dbConnectionData);
48                 reg.SetValue("ENCKEY", dbkey);
49                 reg.SetValue("EMAILADDR", emailaddr);
50                 reg.SetValue("EMAILSERVER", emailServer);
51                 reg.SetValue("EMAILFROM", emailFrom);
52                 reg.SetValue("SPLOCATION", splocation);
53                 reg.SetValue("SPUNAME", spuname);
54                 reg.SetValue("SPPASSWORD", sppassword);
55                 reg.SetValue("DEBUGLOGGING", "NO");
56                 reg.SetValue("PROTECT", dbprotect);
57                 reg.SetValue("WS_UNAME", ws_uname);
58                 reg.SetValue("WS_PASSWORD", ws_password);
59             }
60             
61             //decrypt cs
62             string sKey = reg.GetValue("ENCKEY").ToString();
63             connectionString = reg.GetValue("DB").ToString();
64             emailaddr = reg.GetValue("EMAILADDR").ToString();
65             emailServer = reg.GetValue("EMAILSERVER").ToString();
66             emailFrom = reg.GetValue("EMAILFROM").ToString();
67             splocation = reg.GetValue("SPLOCATION").ToString();
68             spuname = reg.GetValue("SPUNAME").ToString();
69             sppassword = reg.GetValue("SPPASSWORD").ToString();
70             connectionString = crypto.DecryptCS(connectionString, sKey);
71             spuname = crypto.DecryptCS(spuname, sKey);
72             sppassword = crypto.DecryptCS(sppassword, sKey);
73             dbprotect = reg.GetValue("PROTECT").ToString();
74             ws_uname = reg.GetValue("WS_UNAME").ToString();
75             ws_password = reg.GetValue("WS_PASSWORD").ToString();
76 
77             debugLogging = false;
78             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
79             {
80                 debugLogging = true;
81             }
82             
83             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
84             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
85             {
86                 connectionString = reg.GetValue("PROD_DB").ToString();
87             }
88 
89             //deboog
90             //connectionString = "server = grcdslsql0.dom.grc; database = SL_man_App; enlist=false; User ID = sa_config; Password = options23";
91             //end deboog
92         }        
