151         public static string UserName(string id)
152         {
153             string name = "N/A";
154             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
155             using (SqlConnection myConnection = new SqlConnection(connectionString))
156             {
157                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
158                 myCommand.CommandTimeout = 120000;
159                 myConnection.Open();
160                 using (SqlDataReader reader = myCommand.ExecuteReader())
161                 {
162                     if (reader.HasRows)
163                     {
164                         if (reader.Read())
165                         {
166                             name = reader["UNAME"].ToString();
167                         }
168                     }
169                     reader.Close();
170                 }
171                 myConnection.Close();
172             }
173             return name;
174         }
