197         public static void CfgImport(string orderNumber)
198         {
199             //call SP to import data into Syteline
200             int CreateOrder = 1;
201             //string commandtext = "EXEC GR_CfgImportSp '" + orderNumber + "', '" + orderNumber + "', '" + CreateOrder + "' WITH RECOMPILE";
202             //SqlConnection connection = new SqlConnection(connectionString);
203             //SqlCommand command = new SqlCommand(commandtext, connection);
204             //command.CommandTimeout = 120000;
205             //connection.Open();
206             //command.ExecuteNonQuery();
207             //connection.Close();
208 
209             SqlConnection connection = new SqlConnection(connectionString);
210             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
211             command.CommandType = CommandType.StoredProcedure;
212             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
213             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
214             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
215             command.CommandTimeout = 120000;
216             connection.Open();
217             try
218             {
219                 command.ExecuteNonQuery();
220             }
221             catch (Exception spI)
222             {
223                 //A timeout has likely occurred; try again
224                 string logEvent = "An error occurred: " + spI.Message + " -> Retrying...";
225                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
226 
227                 connection.Close();
228                 connection.Open();
229                 command.ExecuteNonQuery();
230             }
231             
232             connection.Close();
233         }
