288         public static string RetrieveSLCO (string orderNum)
289         {
290             string SLOrderNumber = "";
291             //SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
292             SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with (nolock) Where c.order_num = " + (char)39 + orderNum + (char)39;
293             using (SqlConnection myConnection = new SqlConnection(connectionString))
294             {
295                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
296                 myCommand.CommandTimeout = 120000;
297                 myConnection.Open();
298                 using (SqlDataReader reader = myCommand.ExecuteReader())
299                 {
300                     if (reader.HasRows)
301                     {
302                         if (reader.Read())
303                         {
304                             SLOrderNumber = reader["SLCO"].ToString();
305                         }
306                     }
307                     reader.Close();
308                 }
309                 myConnection.Close();
310             }
311             return SLOrderNumber;
312         }
