21         public void SetConnectionString()
22         {
23             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
24             if (reg == null)
25             {
26                 //create keys if they don't already exist and set values
27                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
28                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
29                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
30                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
31                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
32                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
33                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
34                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
35                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
36                 reg.SetValue("DB", dbConnectionData);
37                 reg.SetValue("ENCKEY", dbkey);
38                 reg.SetValue("EMAILADDR", emailaddr);
39                 reg.SetValue("EMAILSERVER", emailServer);
40                 reg.SetValue("EMAILFROM", emailFrom);
41                 reg.SetValue("SPLOCATION", splocation);
42                 reg.SetValue("SPUNAME", spuname);
43                 reg.SetValue("SPPASSWORD", sppassword);
44             }
45             
46             //decrypt cs
47             string sKey = reg.GetValue("ENCKEY").ToString();
48             connectionString = reg.GetValue("DB").ToString();
49             emailaddr = reg.GetValue("EMAILADDR").ToString();
50             emailServer = reg.GetValue("EMAILSERVER").ToString();
51             emailFrom = reg.GetValue("EMAILFROM").ToString();
52             splocation = reg.GetValue("SPLOCATION").ToString();
53             spuname = reg.GetValue("SPUNAME").ToString();
54             sppassword = reg.GetValue("SPPASSWORD").ToString();
55             crypto csec = new crypto();
56             csec.DecryptCS(connectionString, sKey);
57             
58             //deboog
59             //connectionString = "server = grcdslsql0.dom.grc; database = SL_man_App; enlist=false; User ID = sa_config; Password = options23";
60             //end deboog
61         }        
