115         public static string UserName(string id)
116         {
117             string name = "N/A";
118             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
119             using (SqlConnection myConnection = new SqlConnection(connectionString))
120             {
121                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
122                 myConnection.Open();
123                 myCommand.CommandTimeout = 600;
124                 using (SqlDataReader reader = myCommand.ExecuteReader())
125                 {
126                     if (reader.HasRows)
127                     {
128                         if (reader.Read())
129                         {
130                             name = reader["UNAME"].ToString();
131                         }
132                     }
133                     reader.Close();
134                 }
135                 myConnection.Close();
136             }
137             return name;
138         }
139         public static void ResequenceBOM(string orderNumber, int orderLine)
140         {
141             //call SP to resequence BOM records based on parentIDs
142             SqlConnection connection = new SqlConnection(connectionString);
143             SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
144             command.CommandType = CommandType.StoredProcedure;
145             command.Parameters.AddWithValue("@OrderNumber", orderNumber);
146             command.Parameters.AddWithValue("@OrderLine", orderLine);
147             connection.Open();
148             command.CommandTimeout = 600;
149             command.ExecuteNonQuery();
150             connection.Close();
151         }
152         public static void CfgImport(string orderNumber)
153         {
154             //call SP to import data into Syteline
155             int CreateOrder = 1;
156             SqlConnection connection = new SqlConnection(connectionString);
157             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
158             command.CommandType = CommandType.StoredProcedure;
159             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
160             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
161             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
162             connection.Open();
163             command.CommandTimeout = 600;
164             command.ExecuteNonQuery();
165             connection.Close();
166         }
