215         public static string RetrieveSLCO (string orderNum)
216         {
217             string SLOrderNumber = "";
218             SQLCommand = "Select TOP 1 co_num as SLCO From CO c Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
219             using (SqlConnection myConnection = new SqlConnection(connectionString))
220             {
221                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
222                 myConnection.Open();
223                 using (SqlDataReader reader = myCommand.ExecuteReader())
224                 {
225                     if (reader.HasRows)
226                     {
227                         if (reader.Read())
228                         {
229                             SLOrderNumber = reader["SLCO"].ToString();
230                         }
231                     }
232                     reader.Close();
233                 }
234                 myConnection.Close();
235             }
236             return SLOrderNumber;
237         }
