18         public void SetConnectionString()
19         {
20             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
21             if (reg == null)
22             {
23                 //create keys if they don't already exist and set values
24                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
25                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
26                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
27                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
28                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
29                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
30                 reg.SetValue("DB", dbConnectionData);
31                 reg.SetValue("ENCKEY", dbkey);
32                 reg.SetValue("EMAILADDR", emailaddr);
33                 reg.SetValue("EMAILSERVER", emailServer);
34                 reg.SetValue("EMAILFROM", emailFrom);
35             }
36             
37             //decrypt cs
38             string sKey = reg.GetValue("ENCKEY").ToString();
39             connectionString = reg.GetValue("DB").ToString();
40             emailaddr = reg.GetValue("EMAILADDR").ToString();
41             emailServer = reg.GetValue("EMAILSERVER").ToString();
42             emailFrom = reg.GetValue("EMAILFROM").ToString();
43             crypto csec = new crypto();
44             csec.DecryptCS(connectionString, sKey);
45             
46             //deboog
47             //connectionString = "server = grcdslsql0.dom.grc; database = SL_man_App; enlist=false; User ID = sa_config; Password = options23";
48             //end deboog
49         }        
