103         public static void ResequenceBOM(string orderNumber, int orderLine)
104         {
105             //call SP to resequence BOM records based on parentIDs
106             SqlConnection connection = new SqlConnection(connectionString);
107             SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
108             command.CommandType = CommandType.StoredProcedure;
109             command.Parameters.AddWithValue("@OrderNumber", orderNumber);
110             command.Parameters.AddWithValue("@OrderLine", orderLine);
111             connection.Open();
112             command.CommandTimeout = 600;
113             command.ExecuteNonQuery();
114             connection.Close();
115         }
116         public static void CfgImport(string orderNumber)
117         {
118             int CreateOrder = 1;
119             //call SP to import data into Syteline
120             SqlConnection connection = new SqlConnection(connectionString);
121             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
122             command.CommandType = CommandType.StoredProcedure;
123             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
124             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
125             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
126             connection.Open();
127             command.CommandTimeout = 600;
128             command.ExecuteNonQuery();
129             connection.Close();
130         }
