38         public void SetConnectionString()
39         {
40             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
41             if (reg == null)
42             {
43                 //create keys if they don't already exist and set values
44                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
45                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
46                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
47                 string proddbConnectionData = File.ReadAllText(@"C:\C1\proddbconnection.dat");
48                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
49                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
50                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
51                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
52                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
53                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
54                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
55                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
56                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
57                 //set registry values with defaults
58                 reg.SetValue("PROD_DB", proddbConnectionData);
59                 reg.SetValue("DB", dbConnectionData);
60                 reg.SetValue("ENCKEY", dbkey);
61                 reg.SetValue("EMAILADDR", emailaddr);
62                 reg.SetValue("EMAILSERVER", emailServer);
63                 reg.SetValue("EMAILFROM", emailFrom);
64                 reg.SetValue("SPLOCATION", splocation);
65                 reg.SetValue("SPUNAME", spuname);
66                 reg.SetValue("SPPASSWORD", sppassword);
67                 reg.SetValue("DEBUGLOGGING", "NO");
68                 reg.SetValue("PROTECT", dbprotect);
69                 reg.SetValue("WS_UNAME", ws_uname);
70                 reg.SetValue("WS_PASSWORD", ws_password);
71             }
72             
73             //decrypt cs
74             string sKey = reg.GetValue("ENCKEY").ToString();
75             connectionString = reg.GetValue("DB").ToString();
76             emailaddr = reg.GetValue("EMAILADDR").ToString();
77             emailServer = reg.GetValue("EMAILSERVER").ToString();
78             emailFrom = reg.GetValue("EMAILFROM").ToString();
79             splocation = reg.GetValue("SPLOCATION").ToString();
80             spuname = reg.GetValue("SPUNAME").ToString();
81             sppassword = reg.GetValue("SPPASSWORD").ToString();
82             connectionString = crypto.DecryptCS(connectionString, sKey);
83             spuname = crypto.DecryptCS(spuname, sKey);
84             sppassword = crypto.DecryptCS(sppassword, sKey);
85             dbprotect = reg.GetValue("PROTECT").ToString();
86             ws_uname = reg.GetValue("WS_UNAME").ToString();
87             ws_password = reg.GetValue("WS_PASSWORD").ToString();
88 
89             debugLogging = false;
90             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
91             {
92                 debugLogging = true;
93             }
94 
95             queueControlConnectionString = queueControlConnectionStringValueDEV;
96             if (reg.GetValue("ENVIRONMENT").ToString() == "PROD") { queueControlConnectionString = queueControlConnectionStringValuePROD; }
97 
98             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
99             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
100             {
101                 connectionString = reg.GetValue("PROD_DB").ToString();
102                 queueControlConnectionString = queueControlConnectionStringValuePROD;   //override ENVIRONMENT if incoming order is production
103             }
104         }        
105         public static void WriteRecordBOM(ref zCfgBOM bom)
106         {
107             SQLCommand = "Insert Into GR_CfgBOM (order_num,order_line_num,seq,parent_id,id,item_num,smartpart_num,unit_price,unit_cost,discount_amt,quantity,priority_level,recsequence) values (" + (char)39 + bom.CO_Num + (char)39 + "," + (char)39 + bom.CO_Line + (char)39 + "," + (char)39 + bom.Sequence + (char)39 + "," + (char)39 + bom.Parent + (char)39 + "," + (char)39 + bom.Identifier.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:id", ref bom)) + (char)39 + "," + (char)39 + bom.Item.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:item_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.Smartpart.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:smartpart_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.UnitPrice + (char)39 + "," + (char)39 + bom.UnitCost + (char)39 + "," + (char)39 + bom.Discount + (char)39 + "," + (char)39 + bom.QTY + (char)39 + "," + (char)39 + bom.PriorityLevel + (char)39 + "," + (char)39 + bom.RecordSequence + (char)39 + ")";
108             using (SqlConnection myConnection = new SqlConnection(connectionString))
109             {
110                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
111                 myCommand.CommandTimeout = 120000;
112                 myConnection.Open();
113                 myCommand.ExecuteNonQuery();
114             }
