351         public static string UserName(string id)
352         {
353             string name = "N/A";
354             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
355             using (SqlConnection myConnection = new SqlConnection(connectionString))
356             {
357                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
358                 myCommand.CommandTimeout = 120000;
359                 myConnection.Open();
360                 using (SqlDataReader reader = myCommand.ExecuteReader())
361                 {
362                     if (reader.HasRows)
363                     {
364                         if (reader.Read())
365                         {
366                             name = reader["UNAME"].ToString();
367                         }
368                     }
369                     reader.Close();
370                 }
371                 myConnection.Close();
372             }
373             return name;
374         }
375         public static void ResequenceBOM(string orderNumber, int orderLine)
376         {
377             try
378             {
379                 //call SP to resequence BOM records based on parentIDs
380                 SqlConnection connection = new SqlConnection(connectionString);
381                 SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
382                 command.CommandType = CommandType.StoredProcedure;
383                 command.Parameters.AddWithValue("@OrderNumber", orderNumber);
384                 command.Parameters.AddWithValue("@OrderLine", orderLine);
385                 command.CommandTimeout = 120000;
386 
387                 connection.Open();
388                 command.ExecuteNonQuery();
389                 connection.Close();
390             }
391             catch (Exception bomreseq)
392             {
393                 string logEvent = "An error resequence-BOM occurred: " + bomreseq.Message;
394                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
395             }
396             
397         }
398         public static void CfgImport(string orderNumber)
399         {
400             //call SP to import data into Syteline
401             int CreateOrder = 1;
402             
403             SqlConnection connection = new SqlConnection(connectionString);
404             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
405             command.CommandType = CommandType.StoredProcedure;
406             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
407             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
408             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
409             command.CommandTimeout = 120000;
410             connection.Open();
411             try
412             {
413                 command.ExecuteNonQuery();
414             }
415             catch (Exception spI)
416             {
417                 //A timeout has likely occurred; try again
418                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
419                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
420 
421                 connection.Close();
422                 connection.Open();
423                 command.ExecuteNonQuery();
424             }
425             
426             connection.Close();
427         }
