617         public static int CoLines(string SLOrderNumber)
618         {
619             int coitems = 0;
620             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = " + (char)39 + SLOrderNumber + (char)39;
621             using (SqlConnection myConnection = new SqlConnection(connectionString))
622             {
623                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
624                 myCommand.CommandTimeout = 120000;
625                 myConnection.Open();
626                 using (SqlDataReader reader = myCommand.ExecuteReader())
627                 {
628                     reader.Read();
629                     coitems = Convert.ToInt16(reader["lines"].ToString());
630                     reader.Close();
631                 }
632                 myConnection.Close();
633             }
634             return coitems;
