636         public static void ExecutePreCache()
637         {
638             //Execute Pre-caching of the SP's to force an on-demand recompile while we are preparing
639             int CreateOrder = 1;
640 
641             SqlConnection connection = new SqlConnection(connectionString);
642             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
643             command.CommandType = CommandType.StoredProcedure;
644             command.Parameters.AddWithValue("@pStartingOrderNum", "PRECACHE");
645             command.Parameters.AddWithValue("@pEndingOrderNum", "PRECACHE");
646             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
647             command.CommandTimeout = 120000;
648             
649             connection.Open();
650             command.ExecuteNonQuery();
651             connection.Close();
652         }
