677         public static string CustomerOnHold(string custnum, string seq)
678         {
679             string holdReason = "";
680             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = " + (char)39 + custnum + (char)39 + " and cust_seq = " + (char)39 + seq + (char)39 + " and uf_cohold = 1";
681             using (SqlConnection myConnection = new SqlConnection(connectionString))
682             {
683                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
684                 myCommand.CommandTimeout = 120000;
685                 myConnection.Open();
686                 using (SqlDataReader reader = myCommand.ExecuteReader())
687                 {
688                     if (reader.HasRows)
689                     {
690                         if (reader.Read())
691                         {
692                             holdReason = reader["uf_coholddescription"].ToString();
693                             Triggers.logEvent = "Found an on-hold record match";
694                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
695                         }
696                     }
697                     reader.Close();
698                 }
699                 myConnection.Close();
700             }
701             return holdReason;
702         }
