738         public static void MoveQueueRecord(string orderNum, string dbSite)
739         {
740             try
741             {
742                 //Disable trigger on target database/table, copy queue record, delete from NOVB
743                 SQLCommand = "DISABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
744                 using (SqlConnection myConnection = new SqlConnection(connectionString))
745                 {
746                     SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
747                     myCommand.CommandTimeout = 120000;
748                     myConnection.Open();
749                     myCommand.ExecuteNonQuery();
750                 }
751 
752                 string recordRowPointer = "";
753                 SQLCommand = "SELECT TOP 1 rowpointer from SL_NOVB_App.dbo.GR_Cfg_Queue where order_num = " + (char)39 + orderNum + (char)39 + " order by recorddate desc";
754                 using (SqlConnection myConnection = new SqlConnection(connectionString))
755                 {
756                     SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
757                     myCommand.CommandTimeout = 120000;
758                     myConnection.Open();
759                     using (SqlDataReader reader = myCommand.ExecuteReader())
760                     {
761                         if (reader.HasRows)
762                         {
763                             if (reader.Read())              //this should never NOT happen (ok that's a little obtuse.  How about: This should ALWAYS happen)
764                             {
765                                 //Copy this record to the target site DB
766                                 recordRowPointer = reader["rowpointer"].ToString();
767                                 reader.Close();
768 
769                                 SQLCommand = "insert into SL_" + dbSite + "_App.dbo.GR_Cfg_Queue Select * from SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = " + (char)39 + recordRowPointer + (char)39;
770                                 using (SqlConnection myConnection2 = new SqlConnection(connectionString))
771                                 {
772                                     SqlCommand myCommand2 = new SqlCommand(SQLCommand, myConnection);
773                                     myCommand2.CommandTimeout = 120000;
774                                     myCommand2.ExecuteNonQuery();
775                                 }
776 
777                                 SQLCommand = "DELETE From SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = " + (char)39 + recordRowPointer + (char)39;
778                                 using (SqlConnection myConnection3 = new SqlConnection(connectionString))
779                                 {
780                                     SqlCommand myCommand3 = new SqlCommand(SQLCommand, myConnection3);
781                                     myCommand3.CommandTimeout = 120000;
782                                     myConnection3.Open();
783                                     myCommand3.ExecuteNonQuery();
784                                     myConnection3.Close();
785                                 }
786                             }
787                         }
788                     }
789                 }
790                 Triggers.logEvent = "Queue record was moved to SL_" + StagingUtilities.dbSite + "_App.dbo.GR_Cfg_Queue";
791                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
792             }
793             catch (Exception mqEx)
794             {
795                 //Log and ignore the error and move on with re-enabling the trigger
796                 string logEvent = "An error occurred while attempting to move the queue record to " + StagingUtilities.dbSite + ": " + mqEx.Message;
797                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
798             }
799             
800             //Re-enable trigger on db site queue table
801             SQLCommand = "ENABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
802             using (SqlConnection myConnection = new SqlConnection(connectionString))
803             {
804                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
805                 myCommand.CommandTimeout = 120000;
806                 myConnection.Open();
807                 myCommand.ExecuteNonQuery();
808             }
809         }
810     }
