158         public static string UserName(string id)
159         {
160             string name = "N/A";
161             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
162             using (SqlConnection myConnection = new SqlConnection(connectionString))
163             {
164                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
165                 myCommand.CommandTimeout = 120000;
166                 myConnection.Open();
167                 using (SqlDataReader reader = myCommand.ExecuteReader())
168                 {
169                     if (reader.HasRows)
170                     {
171                         if (reader.Read())
172                         {
173                             name = reader["UNAME"].ToString();
174                         }
175                     }
176                     reader.Close();
177                 }
178                 myConnection.Close();
179             }
180             return name;
181         }
182         public static void ResequenceBOM(string orderNumber, int orderLine)
183         {
184             //call SP to resequence BOM records based on parentIDs
185             SqlConnection connection = new SqlConnection(connectionString);
186             SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
187             command.CommandType = CommandType.StoredProcedure;
188             command.Parameters.AddWithValue("@OrderNumber", orderNumber);
189             command.Parameters.AddWithValue("@OrderLine", orderLine);
190             command.CommandTimeout = 120000;
191            
192             connection.Open();
193             command.ExecuteNonQuery();
194             connection.Close();
195         }
196         public static void CfgImport(string orderNumber)
197         {
198             //call SP to import data into Syteline
199             int CreateOrder = 1;
200             
201             SqlConnection connection = new SqlConnection(connectionString);
202             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
203             command.CommandType = CommandType.StoredProcedure;
204             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
205             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
206             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
207             command.CommandTimeout = 120000;
208             connection.Open();
209             try
210             {
211                 command.ExecuteNonQuery();
212             }
213             catch (Exception spI)
214             {
215                 //A timeout has likely occurred; try again
216                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
217                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
218 
219                 connection.Close();
220                 connection.Open();
221                 command.ExecuteNonQuery();
222             }
223             
224             connection.Close();
225         }
