280         public static string RetrieveSLCO (string orderNum)
281         {
282             string SLOrderNumber = "";
283             //SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
284             SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with (nolock) Where c.order_num = " + (char)39 + orderNum + (char)39;
285             using (SqlConnection myConnection = new SqlConnection(connectionString))
286             {
287                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
288                 myCommand.CommandTimeout = 120000;
289                 myConnection.Open();
290                 using (SqlDataReader reader = myCommand.ExecuteReader())
291                 {
292                     if (reader.HasRows)
293                     {
294                         if (reader.Read())
295                         {
296                             SLOrderNumber = reader["SLCO"].ToString();
297                         }
298                     }
299                     reader.Close();
300                 }
301                 myConnection.Close();
302             }
303             return SLOrderNumber;
304         }
