375         public static void InsertDocumentRecord(XmlDocument xml, string orderNum, string environment, string site, string SLorderNumber)
376         {
377             using (SqlConnection myConnection = new SqlConnection(queueControlConnectionString))
378             {
379                 myConnection.Open();
380                 SqlCommand SQLCommand = new SqlCommand("Insert Into GR_Cfg_DocumentQueue (order_num, environment, site, SLOrderNumber, orderXML) values (" + (char)39 + orderNum + (char)39 + "," + (char)39 + environment + (char)39 + "," + (char)39 + site + (char)39 + "," + (char)39 + SLorderNumber + (char)39 + ", @xmldata)", myConnection);
381                 SQLCommand.Parameters.Add(new SqlParameter("@xmldata", System.Data.SqlDbType.Xml) { Value = new SqlXml(new XmlTextReader(xml.InnerXml, XmlNodeType.Document, null)) });
382                 SQLCommand.CommandTimeout = 120000;
383                 SQLCommand.ExecuteNonQuery();
384             }
