386         public static bool OrderCompleted(string order_num)
387         {
388             bool rowExists = false;
389             SQLCommand = "Select order_num, co_num, stat from GR_CfgCO with(nolock) where order_num = " + (char)39 + order_num + (char)39 + " and stat = 'C' and co_num is not null";
390             using (SqlConnection myConnection = new SqlConnection(connectionString))
391             {
392                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
393                 myCommand.CommandTimeout = 120000;
394                 myConnection.Open();
395                 using (SqlDataReader reader = myCommand.ExecuteReader())
396                 {
397                     if (reader.HasRows)
398                     {
399                         rowExists = true;
400                     }
401                     reader.Close();
402                 }
403                 myConnection.Close();
404             }
405             return rowExists;
406         }
407         public static int CoLines(string SLOrderNumber)
408         {
409             int coitems = 0;
410             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = " + (char)39 + SLOrderNumber + (char)39;
411             using (SqlConnection myConnection = new SqlConnection(connectionString))
412             {
413                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
414                 myCommand.CommandTimeout = 120000;
415                 myConnection.Open();
416                 using (SqlDataReader reader = myCommand.ExecuteReader())
417                 {
418                     reader.Read();
419                     coitems = Convert.ToInt16(reader["lines"].ToString());
420                     reader.Close();
421                 }
422                 myConnection.Close();
423             }
424             return coitems;
425         }
426         public static void ExecutePreCache()
427         {
428             //Execute Pre-caching of the SP's to force an on-demand recompile while we are preparing
429             int CreateOrder = 1;
430 
431             SqlConnection connection = new SqlConnection(connectionString);
432             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
433             command.CommandType = CommandType.StoredProcedure;
434             command.Parameters.AddWithValue("@pStartingOrderNum", "PRECACHE");
435             command.Parameters.AddWithValue("@pEndingOrderNum", "PRECACHE");
436             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
437             command.CommandTimeout = 120000;
438             
439             connection.Open();
440             command.ExecuteNonQuery();
441             connection.Close();
442         }
