467         public static string CustomerOnHold(string custnum, string seq)
468         {
469             string holdReason = "";
470             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = " + (char)39 + custnum + (char)39 + " and cust_seq = " + (char)39 + seq + (char)39 + " and uf_cohold = 1";
471             using (SqlConnection myConnection = new SqlConnection(connectionString))
472             {
473                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
474                 myCommand.CommandTimeout = 120000;
475                 myConnection.Open();
476                 using (SqlDataReader reader = myCommand.ExecuteReader())
477                 {
478                     if (reader.HasRows)
479                     {
480                         if (reader.Read())
481                         {
482                             holdReason = reader["uf_coholddescription"].ToString();
483                             Triggers.logEvent = "Found an on-hold record match";
484                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
485                         }
486                     }
487                     reader.Close();
488                 }
489                 myConnection.Close();
490             }
491             return holdReason;
492         }
