233         public static void CfgImport(string orderNumber)
234         {
235             //call SP to import data into Syteline
236             int CreateOrder = 1;
237             
238             SqlConnection connection = new SqlConnection(connectionString);
239             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
240             command.CommandType = CommandType.StoredProcedure;
241             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
242             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
243             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
244             command.CommandTimeout = 120000;
245             connection.Open();
246             try
247             {
248                 command.ExecuteNonQuery();
249             }
250             catch (Exception spI)
251             {
252                 //A timeout has likely occurred; try again
253                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
254                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
255 
256                 connection.Close();
257                 connection.Open();
258                 command.ExecuteNonQuery();
259             }
260             
261             connection.Close();
262         }
