491             SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + C1orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
492             using (SqlConnection myConnection = new SqlConnection(connectionString))
493             {
494                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
495                 myCommand.CommandTimeout = 120000;
496                 myConnection.Open();
497                 using (SqlDataReader reader = myCommand.ExecuteReader())
498                 {
499                     if (reader.HasRows)
500                     {
501                         if (reader.Read())
502                         {
503                             orderExists = true;
504                         }
505                     }
506                     reader.Close();
507                 }
508                 myConnection.Close();
509             }
510             return orderExists;
511         }
512         public static string CustomerOnHold(string custnum, string seq)
513         {
514             string holdReason = "";
515             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = " + (char)39 + custnum + (char)39 + " and cust_seq = " + (char)39 + seq + (char)39 + " and uf_cohold = 1";
516             using (SqlConnection myConnection = new SqlConnection(connectionString))
517             {
518                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
519                 myCommand.CommandTimeout = 120000;
520                 myConnection.Open();
521                 using (SqlDataReader reader = myCommand.ExecuteReader())
522                 {
523                     if (reader.HasRows)
524                     {
525                         if (reader.Read())
526                         {
527                             holdReason = reader["uf_coholddescription"].ToString();
528                             Triggers.logEvent = "Found an on-hold record match";
529                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
530                         }
531                     }
532                     reader.Close();
533                 }
534                 myConnection.Close();
535             }
536             return holdReason;
537         }
