24         public static string spuname = "";
25         public static string sppassword = "";
26         public static string decryptedValue = "";
27         public static bool debugLogging = false;
28         public static string dbprotect = "";
29         public static string ws_uname = "";
30         public static string ws_password = "";
31         public static string queueControlConnectionString = "";
32         public static string queueControlConnectionStringValueDEV = "server = grctslsql0.dom.grc; database = GR_C1_DocumentQueue; enlist=false; User ID = sa_config; Password = options23";
33         public static string queueControlConnectionStringValuePROD = "server = grcpslsql0.dom.grc; database = GR_C1_DocumentQueue; enlist=false; User ID = sa_config; Password = options23";
34         public void SetConnectionString()
35         {
36             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
37             if (reg == null)
38             {
39                 //create keys if they don't already exist and set values
40                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
41                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
42                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
43                 string proddbConnectionData = File.ReadAllText(@"C:\C1\proddbconnection.dat");
44                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
45                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
46                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
47                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
48                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
49                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
50                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
51                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
52                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
53                 //set registry values with defaults
54                 reg.SetValue("PROD_DB", proddbConnectionData);
55                 reg.SetValue("DB", dbConnectionData);
56                 reg.SetValue("ENCKEY", dbkey);
57                 reg.SetValue("EMAILADDR", emailaddr);
58                 reg.SetValue("EMAILSERVER", emailServer);
59                 reg.SetValue("EMAILFROM", emailFrom);
60                 reg.SetValue("SPLOCATION", splocation);
61                 reg.SetValue("SPUNAME", spuname);
62                 reg.SetValue("SPPASSWORD", sppassword);
63                 reg.SetValue("DEBUGLOGGING", "NO");
64                 reg.SetValue("PROTECT", dbprotect);
65                 reg.SetValue("WS_UNAME", ws_uname);
66                 reg.SetValue("WS_PASSWORD", ws_password);
67             }
68             
69             //decrypt cs
70             string sKey = reg.GetValue("ENCKEY").ToString();
71             connectionString = reg.GetValue("DB").ToString();
72             emailaddr = reg.GetValue("EMAILADDR").ToString();
73             emailServer = reg.GetValue("EMAILSERVER").ToString();
74             emailFrom = reg.GetValue("EMAILFROM").ToString();
75             splocation = reg.GetValue("SPLOCATION").ToString();
76             spuname = reg.GetValue("SPUNAME").ToString();
77             sppassword = reg.GetValue("SPPASSWORD").ToString();
78             connectionString = crypto.DecryptCS(connectionString, sKey);
79             spuname = crypto.DecryptCS(spuname, sKey);
80             sppassword = crypto.DecryptCS(sppassword, sKey);
81             dbprotect = reg.GetValue("PROTECT").ToString();
82             ws_uname = reg.GetValue("WS_UNAME").ToString();
83             ws_password = reg.GetValue("WS_PASSWORD").ToString();
84 
85             debugLogging = false;
86             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
87             {
88                 debugLogging = true;
89             }
90 
91             queueControlConnectionString = queueControlConnectionStringValueDEV;
92             if (reg.GetValue("ENVIRONMENT").ToString() == "PROD") { queueControlConnectionString = queueControlConnectionStringValuePROD; }
93 
94             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
95             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
96             {
97                 connectionString = reg.GetValue("PROD_DB").ToString();
98                 queueControlConnectionString = queueControlConnectionStringValuePROD;   //override ENVIRONMENT if incoming order is production
99             }
100         }        
101         public static void WriteRecordBOM(ref zCfgBOM bom)
102         {
103             SQLCommand = "Insert Into GR_CfgBOM (order_num,order_line_num,seq,parent_id,id,item_num,smartpart_num,unit_price,unit_cost,discount_amt,quantity,priority_level,recsequence) values (" + (char)39 + bom.CO_Num + (char)39 + "," + (char)39 + bom.CO_Line + (char)39 + "," + (char)39 + bom.Sequence + (char)39 + "," + (char)39 + bom.Parent + (char)39 + "," + (char)39 + bom.Identifier.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:id", ref bom)) + (char)39 + "," + (char)39 + bom.Item.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:item_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.Smartpart.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:smartpart_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.UnitPrice + (char)39 + "," + (char)39 + bom.UnitCost + (char)39 + "," + (char)39 + bom.Discount + (char)39 + "," + (char)39 + bom.QTY + (char)39 + "," + (char)39 + bom.PriorityLevel + (char)39 + "," + (char)39 + bom.RecordSequence + (char)39 + ")";
104             using (SqlConnection myConnection = new SqlConnection(connectionString))
105             {
106                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
107                 myCommand.CommandTimeout = 120000;
108                 myConnection.Open();
109                 myCommand.ExecuteNonQuery();
110             }
111         }
