192                     myCommand.ExecuteNonQuery();
193                 }
194             }
195             catch (Exception RBError)
196             {
197                 Triggers.logEvent = "ERROR WRITING GR_CfgRouteBOM: " + RBError.Message;
198                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
199             }
200         }
