225         public static void ResequenceBOM(string orderNumber, int orderLine)
226         {
227             try
228             {
229                 //call SP to resequence BOM records based on parentIDs
230                 SqlConnection connection = new SqlConnection(connectionString);
231                 SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
232                 command.CommandType = CommandType.StoredProcedure;
233                 command.Parameters.AddWithValue("@OrderNumber", orderNumber);
234                 command.Parameters.AddWithValue("@OrderLine", orderLine);
235                 command.CommandTimeout = 120000;
236 
237                 connection.Open();
238                 command.ExecuteNonQuery();
239                 connection.Close();
240             }
241             catch (Exception bomreseq)
242             {
243                 string logEvent = "An error resequence-BOM occurred: " + bomreseq.Message;
244                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
245             }
246             
247         }
248         public static void CfgImport(string orderNumber)
249         {
250             //call SP to import data into Syteline
251             int CreateOrder = 1;
252             
253             SqlConnection connection = new SqlConnection(connectionString);
254             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
255             command.CommandType = CommandType.StoredProcedure;
256             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
257             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
258             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
259             command.CommandTimeout = 120000;
260             connection.Open();
261             try
262             {
263                 command.ExecuteNonQuery();
264             }
265             catch (Exception spI)
266             {
267                 //A timeout has likely occurred; try again
268                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
269                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
270 
271                 connection.Close();
272                 connection.Open();
273                 command.ExecuteNonQuery();
274             }
275             
276             connection.Close();
277         }
