344             SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with (nolock) Where c.order_num = " + (char)39 + orderNum + (char)39;
345             using (SqlConnection myConnection = new SqlConnection(connectionString))
346             {
347                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
348                 myCommand.CommandTimeout = 120000;
349                 myConnection.Open();
350                 using (SqlDataReader reader = myCommand.ExecuteReader())
351                 {
352                     if (reader.HasRows)
353                     {
354                         if (reader.Read())
355                         {
356                             SLOrderNumber = reader["SLCO"].ToString();
357                         }
358                     }
