446         public static bool OrderCompleted(string order_num)
447         {
448             bool rowExists = false;
449             SQLCommand = "Select order_num, co_num, stat from GR_CfgCO with(nolock) where order_num = " + (char)39 + order_num + (char)39 + " and stat = 'C' and co_num is not null";
450             using (SqlConnection myConnection = new SqlConnection(connectionString))
451             {
452                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
453                 myCommand.CommandTimeout = 120000;
454                 myConnection.Open();
455                 using (SqlDataReader reader = myCommand.ExecuteReader())
456                 {
457                     if (reader.HasRows)
458                     {
459                         rowExists = true;
460                     }
461                     reader.Close();
462                 }
463                 myConnection.Close();
464             }
465             return rowExists;
