467         public static int CoLines(string SLOrderNumber)
468         {
469             int coitems = 0;
470             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = " + (char)39 + SLOrderNumber + (char)39;
471             using (SqlConnection myConnection = new SqlConnection(connectionString))
472             {
473                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
474                 myCommand.CommandTimeout = 120000;
475                 myConnection.Open();
476                 using (SqlDataReader reader = myCommand.ExecuteReader())
477                 {
478                     reader.Read();
479                     coitems = Convert.ToInt16(reader["lines"].ToString());
480                     reader.Close();
481                 }
482                 myConnection.Close();
483             }
484             return coitems;
485         }
486         public static void ExecutePreCache()
487         {
488             //Execute Pre-caching of the SP's to force an on-demand recompile while we are preparing
489             int CreateOrder = 1;
490 
491             SqlConnection connection = new SqlConnection(connectionString);
492             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
493             command.CommandType = CommandType.StoredProcedure;
494             command.Parameters.AddWithValue("@pStartingOrderNum", "PRECACHE");
495             command.Parameters.AddWithValue("@pEndingOrderNum", "PRECACHE");
496             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
497             command.CommandTimeout = 120000;
498             
499             connection.Open();
500             command.ExecuteNonQuery();
501             connection.Close();
502         }
