527         public static string CustomerOnHold(string custnum, string seq)
528         {
529             string holdReason = "";
530             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = " + (char)39 + custnum + (char)39 + " and cust_seq = " + (char)39 + seq + (char)39 + " and uf_cohold = 1";
531             using (SqlConnection myConnection = new SqlConnection(connectionString))
532             {
533                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
534                 myCommand.CommandTimeout = 120000;
535                 myConnection.Open();
536                 using (SqlDataReader reader = myCommand.ExecuteReader())
537                 {
538                     if (reader.HasRows)
539                     {
540                         if (reader.Read())
541                         {
542                             holdReason = reader["uf_coholddescription"].ToString();
543                             Triggers.logEvent = "Found an on-hold record match";
544                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
545                         }
546                     }
547                     reader.Close();
548                 }
549                 myConnection.Close();
550             }
551             return holdReason;
552         }
