588         public static void MoveQueueRecord(string orderNum, string dbSite)
589         {
590             try
591             {
592                 //Disable trigger on target database/table, copy queue record, delete from NOVB
593                 SQLCommand = "DISABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
594                 using (SqlConnection myConnection = new SqlConnection(connectionString))
595                 {
596                     SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
597                     myCommand.CommandTimeout = 120000;
598                     myConnection.Open();
599                     myCommand.ExecuteNonQuery();
600                 }
601 
602                 string recordRowPointer = "";
603                 SQLCommand = "SELECT TOP 1 rowpointer from SL_NOVB_App.dbo.GR_Cfg_Queue where order_num = " + (char)39 + orderNum + (char)39 + " order by recorddate desc";
604                 using (SqlConnection myConnection = new SqlConnection(connectionString))
605                 {
606                     SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
607                     myCommand.CommandTimeout = 120000;
608                     myConnection.Open();
609                     using (SqlDataReader reader = myCommand.ExecuteReader())
610                     {
611                         if (reader.HasRows)
612                         {
613                             if (reader.Read())              //this should never NOT happen (ok that's a little obtuse.  How about: This should ALWAYS happen)
614                             {
615                                 //Copy this record to the target site DB
616                                 recordRowPointer = reader["rowpointer"].ToString();
617                                 reader.Close();
618 
619                                 SQLCommand = "insert into SL_" + dbSite + "_App.dbo.GR_Cfg_Queue Select * from SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = " + (char)39 + recordRowPointer + (char)39;
620                                 using (SqlConnection myConnection2 = new SqlConnection(connectionString))
621                                 {
622                                     SqlCommand myCommand2 = new SqlCommand(SQLCommand, myConnection);
623                                     myCommand2.CommandTimeout = 120000;
624                                     myCommand2.ExecuteNonQuery();
625                                 }
626 
627                                 SQLCommand = "DELETE From SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = " + (char)39 + recordRowPointer + (char)39;
628                                 using (SqlConnection myConnection3 = new SqlConnection(connectionString))
629                                 {
630                                     SqlCommand myCommand3 = new SqlCommand(SQLCommand, myConnection3);
631                                     myCommand3.CommandTimeout = 120000;
632                                     myConnection3.Open();
633                                     myCommand3.ExecuteNonQuery();
634                                     myConnection3.Close();
635                                 }
636                             }
637                         }
638                     }
639                 }
640                 Triggers.logEvent = "Queue record was moved to SL_" + StagingUtilities.dbSite + "_App.dbo.GR_Cfg_Queue";
641                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
642             }
643             catch (Exception mqEx)
644             {
645                 //Log and ignore the error and move on with re-enabling the trigger
646                 string logEvent = "An error occurred while attempting to move the queue record to " + StagingUtilities.dbSite + ": " + mqEx.Message;
647                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
648             }
649             
650             //Re-enable trigger on db site queue table
651             SQLCommand = "ENABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
652             using (SqlConnection myConnection = new SqlConnection(connectionString))
653             {
654                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
655                 myCommand.CommandTimeout = 120000;
656                 myConnection.Open();
657                 myCommand.ExecuteNonQuery();
658             }
659         }
660     }
661 }
