24         public void SetConnectionString()
25         {
26             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
27             if (reg == null)
28             {
29                 //create keys if they don't already exist and set values
30                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
31                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
32                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
33                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
34                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
35                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
36                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
37                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
38                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
39                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
40                 reg.SetValue("PROD_DB", @"C:\C1\proddbconnection.dat");
41                 reg.SetValue("DB", dbConnectionData);
42                 reg.SetValue("ENCKEY", dbkey);
43                 reg.SetValue("EMAILADDR", emailaddr);
44                 reg.SetValue("EMAILSERVER", emailServer);
45                 reg.SetValue("EMAILFROM", emailFrom);
46                 reg.SetValue("SPLOCATION", splocation);
47                 reg.SetValue("SPUNAME", spuname);
48                 reg.SetValue("SPPASSWORD", sppassword);
49                 reg.SetValue("DEBUGLOGGING", "NO");
50                 reg.SetValue("PROTECT", dbprotect);
51             }
52             
53             //decrypt cs
54             string sKey = reg.GetValue("ENCKEY").ToString();
55             connectionString = reg.GetValue("DB").ToString();
56             emailaddr = reg.GetValue("EMAILADDR").ToString();
57             emailServer = reg.GetValue("EMAILSERVER").ToString();
58             emailFrom = reg.GetValue("EMAILFROM").ToString();
59             splocation = reg.GetValue("SPLOCATION").ToString();
60             spuname = reg.GetValue("SPUNAME").ToString();
61             sppassword = reg.GetValue("SPPASSWORD").ToString();
62             connectionString = crypto.DecryptCS(connectionString, sKey);
63             spuname = crypto.DecryptCS(spuname, sKey);
64             sppassword = crypto.DecryptCS(sppassword, sKey);
65             debugLogging = false;
66             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
67             {
68                 debugLogging = true;
69             }
70 
71             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
72             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
73             {
74                 connectionString = reg.GetValue("PROD_DB").ToString();
75             }
76 
77             //deboog
78             //connectionString = "server = grcdslsql0.dom.grc; database = SL_man_App; enlist=false; User ID = sa_config; Password = options23";
79             //end deboog
80         }        
