83             SQLCommand = "Insert Into GR_CfgBOM (order_num,order_line_num,seq,parent_id,id,item_num,smartpart_num,unit_price,unit_cost,discount_amt,quantity,priority_level,recsequence) values (" + (char)39 + bom.CO_Num + (char)39 + "," + (char)39 + bom.CO_Line + (char)39 + "," + (char)39 + bom.Sequence + (char)39 + "," + (char)39 + bom.Parent + (char)39 + "," + (char)39 + bom.Identifier.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:id", ref bom)) + (char)39 + "," + (char)39 + bom.Item.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:item_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.Smartpart.Substring(0, C1Dictionaries.DBFieldLenBOM("BOM:smartpart_num", ref bom)).Replace("'","''") + (char)39 + "," + (char)39 + bom.UnitPrice + (char)39 + "," + (char)39 + bom.UnitCost + (char)39 + "," + (char)39 + bom.Discount + (char)39 + "," + (char)39 + bom.QTY + (char)39 + "," + (char)39 + bom.PriorityLevel + (char)39 + "," + (char)39 + bom.RecordSequence + (char)39 + ")";
