139         public static string UserName(string id)
140         {
141             string name = "N/A";
142             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
143             using (SqlConnection myConnection = new SqlConnection(connectionString))
144             {
145                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
146                 myCommand.CommandTimeout = 120000;
147                 myConnection.Open();
148                 myCommand.CommandTimeout = 600;
149                 using (SqlDataReader reader = myCommand.ExecuteReader())
150                 {
151                     if (reader.HasRows)
152                     {
153                         if (reader.Read())
154                         {
155                             name = reader["UNAME"].ToString();
156                         }
157                     }
158                     reader.Close();
159                 }
160                 myConnection.Close();
161             }
162             return name;
163         }
