262         public static string RetrieveSLCO (string orderNum)
263         {
264             string SLOrderNumber = "";
265             SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
266             using (SqlConnection myConnection = new SqlConnection(connectionString))
267             {
268                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
269                 myCommand.CommandTimeout = 120000;
270                 myConnection.Open();
271                 using (SqlDataReader reader = myCommand.ExecuteReader())
272                 {
273                     if (reader.HasRows)
274                     {
275                         if (reader.Read())
276                         {
277                             SLOrderNumber = reader["SLCO"].ToString();
278                         }
279                     }
280                     reader.Close();
281                 }
282                 myConnection.Close();
283             }
284             return SLOrderNumber;
285         }
286         public static string RetrieveISOCountry(string ISOCode)
287         {
288             string ISOCountry = "";
289             SQLCommand = "Select IsNULL(country, ' ') as country from country with (nolock) Where iso_country_code = " + (char)39 + ISOCode + (char)39;
290             using (SqlConnection myConnection = new SqlConnection(connectionString))
291             {
292                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
293                 myCommand.CommandTimeout = 120000;
294                 myConnection.Open();
295                 using (SqlDataReader reader = myCommand.ExecuteReader())
296                 {
297                     if (reader.HasRows)
298                     {
299                         if (reader.Read())
300                         {
301                             ISOCountry = reader["country"].ToString();
302                         }
303                     }
304                     reader.Close();
305                 }
306                 myConnection.Close();
307             }
308             return ISOCountry;
309         }
