32         public static string queueControlConnectionStringValuePROD = "server = grcpslsql0.dom.grc; database = GR_C1_DocumentQueue; enlist=false; User ID = sa_config; Password = options23";
33         public void SetConnectionString()
34         {
35             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
36             if (reg == null)
37             {
38                 //create keys if they don't already exist and set values
39                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
40                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
41                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
42                 string proddbConnectionData = File.ReadAllText(@"C:\C1\proddbconnection.dat");
43                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
44                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
45                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
46                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
47                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
48                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
49                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
50                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
51                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
52                 //set registry values with defaults
53                 reg.SetValue("PROD_DB", proddbConnectionData);
54                 reg.SetValue("DB", dbConnectionData);
55                 reg.SetValue("ENCKEY", dbkey);
56                 reg.SetValue("EMAILADDR", emailaddr);
57                 reg.SetValue("EMAILSERVER", emailServer);
58                 reg.SetValue("EMAILFROM", emailFrom);
59                 reg.SetValue("SPLOCATION", splocation);
60                 reg.SetValue("SPUNAME", spuname);
61                 reg.SetValue("SPPASSWORD", sppassword);
62                 reg.SetValue("DEBUGLOGGING", "NO");
63                 reg.SetValue("PROTECT", dbprotect);
64                 reg.SetValue("WS_UNAME", ws_uname);
65                 reg.SetValue("WS_PASSWORD", ws_password);
66             }
67             
68             //decrypt cs
69             string sKey = reg.GetValue("ENCKEY").ToString();
70             connectionString = reg.GetValue("DB").ToString();
71             emailaddr = reg.GetValue("EMAILADDR").ToString();
72             emailServer = reg.GetValue("EMAILSERVER").ToString();
73             emailFrom = reg.GetValue("EMAILFROM").ToString();
74             splocation = reg.GetValue("SPLOCATION").ToString();
75             spuname = reg.GetValue("SPUNAME").ToString();
76             sppassword = reg.GetValue("SPPASSWORD").ToString();
77             connectionString = crypto.DecryptCS(connectionString, sKey);
78             spuname = crypto.DecryptCS(spuname, sKey);
79             sppassword = crypto.DecryptCS(sppassword, sKey);
80             dbprotect = reg.GetValue("PROTECT").ToString();
81             ws_uname = reg.GetValue("WS_UNAME").ToString();
82             ws_password = reg.GetValue("WS_PASSWORD").ToString();
83 
84             debugLogging = false;
85             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
86             {
87                 debugLogging = true;
88             }
89 
90             queueControlConnectionString = queueControlConnectionStringValueDEV;
91             if (reg.GetValue("ENVIRONMENT").ToString() == "PROD") { queueControlConnectionString = queueControlConnectionStringValuePROD; }
92 
93             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
94             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
95             {
96                 connectionString = reg.GetValue("PROD_DB").ToString();
97                 queueControlConnectionString = queueControlConnectionStringValuePROD;   //override ENVIRONMENT if incoming order is production
98             }
99         }        
