187         public static string UserName(string id)
188         {
189             string name = "N/A";
190             SQLCommand = "Select cfg_user_name as UNAME From GR_CfgUserID with (nolock) where cfg_user_id like " + (char)39 + "%" + id + "%" + (char)39;
191             using (SqlConnection myConnection = new SqlConnection(connectionString))
192             {
193                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
194                 myCommand.CommandTimeout = 120000;
195                 myConnection.Open();
196                 using (SqlDataReader reader = myCommand.ExecuteReader())
197                 {
198                     if (reader.HasRows)
199                     {
200                         if (reader.Read())
201                         {
202                             name = reader["UNAME"].ToString();
203                         }
204                     }
205                     reader.Close();
206                 }
207                 myConnection.Close();
208             }
209             return name;
210         }
211         public static void ResequenceBOM(string orderNumber, int orderLine)
212         {
213             //call SP to resequence BOM records based on parentIDs
214             SqlConnection connection = new SqlConnection(connectionString);
215             SqlCommand command = new SqlCommand("GR_Cfg_ResequenceBOMSp", connection);
216             command.CommandType = CommandType.StoredProcedure;
217             command.Parameters.AddWithValue("@OrderNumber", orderNumber);
218             command.Parameters.AddWithValue("@OrderLine", orderLine);
219             command.CommandTimeout = 120000;
220            
221             connection.Open();
222             command.ExecuteNonQuery();
223             connection.Close();
224         }
225         public static void CfgImport(string orderNumber)
226         {
227             //call SP to import data into Syteline
228             int CreateOrder = 1;
229             
230             SqlConnection connection = new SqlConnection(connectionString);
231             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
232             command.CommandType = CommandType.StoredProcedure;
233             command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
234             command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
235             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
236             command.CommandTimeout = 120000;
237             connection.Open();
238             try
239             {
240                 command.ExecuteNonQuery();
241             }
242             catch (Exception spI)
243             {
244                 //A timeout has likely occurred; try again
245                 string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
246                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
247 
248                 connection.Close();
249                 connection.Open();
250                 command.ExecuteNonQuery();
251             }
252             
253             connection.Close();
254         }
