309         public static string RetrieveSLCO (string orderNum)
310         {
311             string SLOrderNumber = "";
312             //SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = " + (char)39 + orderNum + (char)39 + " Order By c.order_date desc, c.co_num desc";
313             SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with (nolock) Where c.order_num = " + (char)39 + orderNum + (char)39;
314             using (SqlConnection myConnection = new SqlConnection(connectionString))
315             {
316                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
317                 myCommand.CommandTimeout = 120000;
318                 myConnection.Open();
319                 using (SqlDataReader reader = myCommand.ExecuteReader())
320                 {
321                     if (reader.HasRows)
322                     {
323                         if (reader.Read())
324                         {
