436         public static int CoLines(string SLOrderNumber)
437         {
438             int coitems = 0;
439             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = " + (char)39 + SLOrderNumber + (char)39;
440             using (SqlConnection myConnection = new SqlConnection(connectionString))
441             {
442                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
443                 myCommand.CommandTimeout = 120000;
444                 myConnection.Open();
445                 using (SqlDataReader reader = myCommand.ExecuteReader())
446                 {
447                     reader.Read();
448                     coitems = Convert.ToInt16(reader["lines"].ToString());
449                     reader.Close();
450                 }
451                 myConnection.Close();
452             }
453             return coitems;
454         }
455         public static void ExecutePreCache()
456         {
457             //Execute Pre-caching of the SP's to force an on-demand recompile while we are preparing
458             int CreateOrder = 1;
459 
460             SqlConnection connection = new SqlConnection(connectionString);
461             SqlCommand command = new SqlCommand("GR_CfgImportSp", connection);
462             command.CommandType = CommandType.StoredProcedure;
463             command.Parameters.AddWithValue("@pStartingOrderNum", "PRECACHE");
464             command.Parameters.AddWithValue("@pEndingOrderNum", "PRECACHE");
465             command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
466             command.CommandTimeout = 120000;
467             
468             connection.Open();
469             command.ExecuteNonQuery();
470             connection.Close();
471         }
