496         public static string CustomerOnHold(string custnum, string seq)
497         {
498             string holdReason = "";
499             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = " + (char)39 + custnum + (char)39 + " and cust_seq = " + (char)39 + seq + (char)39 + " and uf_cohold = 1";
500             using (SqlConnection myConnection = new SqlConnection(connectionString))
501             {
502                 SqlCommand myCommand = new SqlCommand(SQLCommand, myConnection);
503                 myCommand.CommandTimeout = 120000;
504                 myConnection.Open();
505                 using (SqlDataReader reader = myCommand.ExecuteReader())
506                 {
507                     if (reader.HasRows)
508                     {
509                         if (reader.Read())
510                         {
511                             holdReason = reader["uf_coholddescription"].ToString();
512                             Triggers.logEvent = "Found an on-hold record match";
513                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
514                         }
515                     }
516                     reader.Close();
517                 }
518                 myConnection.Close();
519             }
520             return holdReason;
521         }
