112 
113                 while (arrayindex < upperBound)
114                 {
115                     documentName = docs[arrayindex];
116                     int strPos = documentName.IndexOf("_");
117                     documentSerialNumber = documentName.Substring(0, strPos);
118                     xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soapenv:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:ws=" + (char)34 + "http://ws.configureone.com" + (char)34 + " xmlns:mod=" + (char)34 + "http://model.ws.configureone.com" + (char)34 + " xmlns:soapenv=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soapenv:Body><ws:" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><ws:document><mod:type>" + documentType + "</mod:type><mod:serialNumber>" + documentSerialNumber + "</mod:serialNumber><mod:files><mod:fileName>" + documentName + "</mod:fileName><mod:content>" + documentName + "</mod:content></mod:files></ws:document></ws:" + key + "></soapenv:Body></soapenv:Envelope>";
119                     sURL = useMethod;
120                     objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
121                     objRequest.Method = "POST";
122                     objRequest.ContentType = "text/xml";
123                     objRequest.Headers.Add("SOAPAction", key);
124                     objRequest.Timeout = 120000;
125                     objRequest.ReadWriteTimeout = 120000;
126                     objRequest.Credentials = new NetworkCredential(DatabaseFactory.ws_uname, DatabaseFactory.ws_password);
127 
128                     logEvent = "DEBUG: Requested document from C1: " + documentName + " Serial#: " + documentSerialNumber;
129                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
130 
131                     startTime = DateTime.Now;
132                     data = new StringBuilder();
133 
134                     data = new StringBuilder();
135                     data.Append(xmlPayload);
136                     byte[] byteDataDoc = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
137                     objRequest.ContentLength = byteDataDoc.Length;
138 
139                     using (Stream postStream = objRequest.GetRequestStream())
140                     {
141                         postStream.ReadTimeout = 120000;
142                         postStream.WriteTimeout = 120000;
143                         postStream.Write(byteDataDoc, 0, byteDataDoc.Length);
144                     }
145                     xmlResult = new XmlDocument();
146                     result = "";
147 
148                     logEvent = "DEBUG: Waiting for response from C1";
149                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
150 
151                     try
152                     {
153                         using (HttpWebResponse response = (HttpWebResponse)objRequest.GetResponse())
154                         {
155                             StreamReader reader = new StreamReader(response.GetResponseStream());
156                             result = reader.ReadToEnd();
157                             reader.Close();
158                             response.Close();
159 
160                             try
161                             {
162                                 xmlResult.LoadXml(result);
163                             }
164                             catch (Exception ex1)
165                             {
166                                 return;
167                             }
168 
169                             logEvent = "DEBUG: Response received from C1";
170                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
171                             
172                             // *** LOG TIME
173                             endTime = DateTime.Now;
174                             ts = endTime.Subtract(startTime);
175                             elapsedTimeMS = ts.Milliseconds;
176                             elapsedTimeSeconds = ts.Seconds;
177                             logEvent = "DEBUG: Document response was received from ConfigureOne in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
178                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
179                             string copyToLocation = "";
180                             bool alreadySent = false;
181                             XmlNodeList xnldoc = xmlResult.GetElementsByTagName("content");
182                             foreach (XmlNode node in xnldoc)
183                             {
184                                 logEvent = "DEBUG: Copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex];
185                                 if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
186 
187                                 //Format Sharepoint directory, create if not exist, and copy document to that location (5 retries)
188                                 for (int r = 0; r < 5; r += 1)
189                                 {
190                                     copyToLocation = DatabaseFactory.COItemitem(C1WebService.SPOrderNumber, documentSerialNumber);
191                                     if (copyToLocation != "") { break; }
192                                     System.Threading.Thread.Sleep(500);
193                                 }
194                                 
195                                 //One last retry if we still did not retrieve the coitem item:
196                                 if (copyToLocation == "")
197                                 {
198                                     copyToLocation = DatabaseFactory.COItemitem(C1WebService.SPOrderNumber, documentSerialNumber);
199                                     if (copyToLocation == "")
200                                     {
201                                         logEvent = "Syteline Coitem Item Could Not Be Retrieved After 6 Retries.  Documents Will Be Copied To The Root Of Order Folder For Order: " + C1WebService.SPOrderNumber;
202                                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
203                                         if (!alreadySent)
204                                         {
205                                             alreadySent = true;
206                                             //SendMail.MailMessage(logEvent, "No Syteline Coitem Item For Order: " + C1WebService.SPOrderNumber);
207                                         }
208                                     }
209                                 }
210 
211                                 //Create directory if not exists
212                                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation + copyToLocation); }
213                                 catch (Exception edi)
214                                 {
215                                     //directory already exists, nothing further needs done
216                                 }
217 
218                                 logEvent = "SharepointCopyLocation Value: " + SharepointCopyLocation;
219                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
220 
221                                 logEvent = "CopyToLocation Value: " + copyToLocation;
222                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
223 
224                                 logEvent = "Full-Path Value: " + SharepointCopyLocation + copyToLocation;
225                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
226 
227                                 logEvent = "Full-Path with file Value: " + SharepointCopyLocation + copyToLocation + "\\" + docs[arrayindex];
228                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
229 
230                                 startTime = DateTime.Now;
231                                 
232                                 try
233                                 {
234                                     byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
235                                     //File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
236                                     File.WriteAllBytes(SharepointCopyLocation + copyToLocation + "\\" + docs[arrayindex], pdfByteArray);
