238 
239                                     // *** LOG TIME
240                                     endTime = DateTime.Now;
241                                     ts = endTime.Subtract(startTime);
242                                     elapsedTimeMS = ts.Milliseconds;
243                                     elapsedTimeSeconds = ts.Seconds;
244                                     logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
245                                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
246                                 }
247                                 catch (Exception ts1)
248                                 {
249                                     logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS1 Exception.  A maximum of 5 retries will be attempted.";
250                                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
251                                     bool successfulDocument = false;
252                                     for (int i = 0; i < 5; i += 1)
253                                     {
254                                         if (successfulDocument) { break; }
255                                         try
256                                         {
257                                             byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
258                                             File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
259                                             documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
260 
261                                             // *** LOG TIME
262                                             endTime = DateTime.Now;
263                                             ts = endTime.Subtract(startTime);
264                                             elapsedTimeMS = ts.Milliseconds;
265                                             elapsedTimeSeconds = ts.Seconds;
266                                             logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
267                                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
268                                             successfulDocument = true;
269                                         }
270                                         catch (Exception rt1)
271                                         {
272                                             logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "rt1 Exception on Retry#: " + (i + 1).ToString();
273                                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
274                                         }
275                                     }
276 
277                                     if (!successfulDocument)
278                                     {
279                                         // *** LOG TIME
280                                         endTime = DateTime.Now;
281                                         ts = endTime.Subtract(startTime);
282                                         elapsedTimeMS = ts.Milliseconds;
283                                         elapsedTimeSeconds = ts.Seconds;
284                                         logEvent = "DEBUG: Time that elapsed before timeout occurred for current document: " + Convert.ToString(elapsedTimeMS) + "ms, " + Convert.ToString(elapsedTimeSeconds) + " s";
285                                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
286                                         //SendMail.MailMessage(logEvent, "TIMEOUT");
287                                     }
288                                 }
289                             }
290                         }
291                     }
