300                 
301                 //Save XML output to SharePoint as well
302                 File.Copy(@"C:\C1\XMLOUTPUTS\" + C1WebService.SPPUBOrderNumber + "_XMLOutput.txt", SharepointCopyLocation + C1WebService.SPPUBOrderNumber + "_XMLOutput.txt", true);
