306 
307                 logEvent = "Retrieved/Saved Following Document Files: " + Environment.NewLine + Environment.NewLine + documentFilesSaved;
308                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
309 
310                 //Update order-status and ref number in C1 to Ordered and SL order#, respectively
311                 key = "updateOrder";
312                 if (Triggers.dbEnvironment == "PROD" && DatabaseFactory.dbprotect != "YES") { key = "updateOrderPROD"; }
313 
314                 useMethod = C1Dictionaries.webmethods[key];
315                 string C1orderNumber = orderNumber;
316                 string C1orderReference = C1WebService.SPOrderNumber;
317                 string C1orderStatus = "OR";              //Ordered
318                 xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soap:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:soap=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soap:Body><" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><param1>" + orderNumber + "</param1><param2>" + C1orderReference + "</param2><param3>" + C1orderStatus + "</param3></" + key + "></soap:Body></soap:Envelope>";
319                 sURL = useMethod;
320                 C1WebService.pubURL = sURL;
321                 C1WebService.pubPayload = xmlPayload;
322                 logEvent = "XML Payload To AXIS: " + xmlPayload;
323                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
324                 objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
325                 objRequest.Method = "POST";
326                 objRequest.ContentType = "text/xml";
327                 objRequest.Headers.Add("SOAPAction", key);
328                 objRequest.Timeout = 120000;
329                 objRequest.ReadWriteTimeout = 120000;
330                 objRequest.Credentials = new NetworkCredential(DatabaseFactory.ws_uname, DatabaseFactory.ws_password);
331                 
332                 data = new StringBuilder();
333                 data.Append(xmlPayload);
334                 byte[] byteDataStatus = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
335                 objRequest.ContentLength = byteDataStatus.Length;
336                 using (Stream postStream = objRequest.GetRequestStream())
337                 {
338                     postStream.ReadTimeout = 120000;
339                     postStream.WriteTimeout = 120000;
340                     postStream.Write(byteDataStatus, 0, byteDataStatus.Length);
341                 }
342 
343                 startTime = DateTime.Now;
344                 
345                 //return response from AXIS (if any)
346                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
347                 {
348                     StreamReader reader = new StreamReader(response.GetResponseStream());
349                     result = reader.ReadToEnd();
350                     reader.Close();
351                     response.Close();
352                 }
353 
354                 try
355                 {
356                     xmlResult.LoadXml(result);
357                     xnl = xmlResult.GetElementsByTagName("success");
358                     foreach (XmlNode node in xnl)
359                     {
360                         switch (node.InnerText)
361                         {
362                             case "true":
363                                 logEvent = "Status-Set Result: Success";
364                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
365                                 break;
366                             default:
367                                 logEvent = "Status-Set Result: Failure";
368                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
369                                 break;
370                         }
371                     }
372                 }
373                 catch (Exception ex2)
374                 {
375                     logEvent = "ERROR LOADING XML FROM WEB SERVICE ON C1 ORDER STATUS SET: " + ex2.Message;
376                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
377                     return;
378                 }
379 
380                 logEvent = "C1 Order Status And Reference Completed For C1 Order: " + orderNumber + " - Syteline Order: " + C1WebService.SPOrderNumber;
381                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
382             }
383             catch (Exception exd)
384             {
385                 logEvent = "ERROR: " + exd.Message + " -> " + exd.Source;
386                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
387             }
388         }
389     }
