14         public static string result;
15         public static void CopyDrawings(XmlDocument xmlResult, string url)
16         {
17             string key;
18             string payload;
19             
20             string logEvent;
21             string xmlPayload;
22             string sURL = url;
23 
24             StringBuilder data = new StringBuilder();
25 
26             //Timing vars
27             DateTime startTime = DateTime.Now;
28             DateTime endTime = DateTime.Now;
29             TimeSpan ts = endTime.Subtract(startTime);
30             decimal elapsedTimeMS = ts.Milliseconds;
31             decimal elapsedTimeSeconds = ts.Seconds;
32             DateTime totalTimeStart = DateTime.Now;
33             DateTime totalTimeStop = DateTime.Now;
34 
35             try
36             {
37                 logEvent = "Retrieving/Saving Document Files For Order: " + Triggers.pubOrderNumber + " -> " + C1WebService.SPOrderNumber;
38                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
39 
40                 //Retrieve documents for order, reusing xmlResult above
41                 string documentType = "Design";
42                 string documentName = "";
43                 string orderNumber = Triggers.pubOrderNumber;
44                 string[] docs;
45                 docs = new string[50];
46                 int arrayindex = 0;
47 
48                 //Retrieve the serialNumber from the XML 
49                 string configSerial = "";
50                 XmlNodeList xnlSN = xmlResult.GetElementsByTagName("serialNumber");
51                 foreach (XmlNode node in xnlSN)
52                 {
53                     configSerial = node.InnerText;
54                 }
55 
56                 string useMethod = "";
57                 key = "getDocument";
58                 useMethod = C1Dictionaries.webmethods[key];
59                 string documentSerialNumber = configSerial;
60                 arrayindex = 0;
61 
62                 XmlNodeList xnl = xmlResult.GetElementsByTagName("fileName");
63                 foreach (XmlNode node in xnl)
64                 {
65                     docs[arrayindex] = node.InnerText;
66                     arrayindex += 1;
67                 }
68                 
69                 logEvent = "DEBUG: Finished building document filename array";
70                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
71 
72                 //We now have an array of document filenames; process by retrieving and saving each file
73                 int upperBound = arrayindex;
74                 arrayindex = 0;
75                 string documentFilesSaved = "";
76                 documentSerialNumber = configSerial;
77 
78                 string SharepointLocation = DatabaseFactory.splocation;
79 
80                 if (Triggers.dbEnvironment == "PROD" && DatabaseFactory.dbprotect != "YES")
81                 {
82                     SharepointLocation = @"\\ecm.gormanrupp.com\ecm\NPC\Oper\ConfOrd\" + StagingUtilities.dbSite + "\\";
83                 }
84                 
85                 //*** deboog TEST PRODUCTION LOCATIONS IN SHAREPOINT
86                 //SharepointLocation = @"\\ecm.gormanrupp.com\ecm\NPC\Oper\ConfOrd\" + StagingUtilities.dbSite + "\\";
87                 //*** end deboog
88 
89                 NetworkShare.DisconnectFromShare(SharepointLocation, true);
90                 NetworkShare.ConnectToShare(SharepointLocation, DatabaseFactory.spuname, DatabaseFactory.sppassword);
91 
92                 logEvent = "DEBUG: SP Share credential fixed.  UNAME: " + DatabaseFactory.spuname + " -> PWD: " + DatabaseFactory.sppassword;
93                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
94 
95                 //Create Sharepoint BASE folder for order# (each line will have its own folder by item#)
96                 string SharepointCopyLocation = SharepointLocation + C1WebService.SPOrderNumber + "\\";
97                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation); }
98                 catch (Exception edi)
99                 {
100                     //directory already exists, nothing further needs done
101                 }
102                 
103                 HttpWebRequest objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
104                 objRequest.Method = "POST";
105                 objRequest.ContentType = "text/xml";
106                 objRequest.Headers.Add("SOAPAction", key);
107                 objRequest.Timeout = 120000;        
108                 objRequest.ReadWriteTimeout = 120000;   
109 
110                 while (arrayindex < upperBound)
111                 {
112                     documentName = docs[arrayindex];
113                     int strPos = documentName.IndexOf("_");
114                     documentSerialNumber = documentName.Substring(0, strPos);
115                     xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soapenv:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:ws=" + (char)34 + "http://ws.configureone.com" + (char)34 + " xmlns:mod=" + (char)34 + "http://model.ws.configureone.com" + (char)34 + " xmlns:soapenv=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soapenv:Body><ws:" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><ws:document><mod:type>" + documentType + "</mod:type><mod:serialNumber>" + documentSerialNumber + "</mod:serialNumber><mod:files><mod:fileName>" + documentName + "</mod:fileName><mod:content>" + documentName + "</mod:content></mod:files></ws:document></ws:" + key + "></soapenv:Body></soapenv:Envelope>";
116                     sURL = useMethod;
117                     objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
118                     objRequest.Method = "POST";
119                     objRequest.ContentType = "text/xml";
120                     objRequest.Headers.Add("SOAPAction", key);
121                     objRequest.Timeout = 120000;
122                     objRequest.ReadWriteTimeout = 120000;
123 
124                     logEvent = "DEBUG: Requested document from C1: " + documentName + " Serial#: " + documentSerialNumber;
125                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
126 
127                     startTime = DateTime.Now;
128                     data = new StringBuilder();
129 
130                     data = new StringBuilder();
131                     data.Append(xmlPayload);
132                     byte[] byteDataDoc = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
133                     objRequest.ContentLength = byteDataDoc.Length;
134 
135                     using (Stream postStream = objRequest.GetRequestStream())
136                     {
137                         postStream.ReadTimeout = 120000;
138                         postStream.WriteTimeout = 120000;
139                         postStream.Write(byteDataDoc, 0, byteDataDoc.Length);
140                     }
141                     xmlResult = new XmlDocument();
142                     result = "";
143 
144                     logEvent = "DEBUG: Waiting for response from C1";
145                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
146 
147                     try
148                     {
149                         using (HttpWebResponse response = (HttpWebResponse)objRequest.GetResponse())
150                         {
151                             StreamReader reader = new StreamReader(response.GetResponseStream());
152                             result = reader.ReadToEnd();
153                             reader.Close();
154                             response.Close();
155 
156                             try
157                             {
158                                 xmlResult.LoadXml(result);
159                             }
160                             catch (Exception ex1)
161                             {
162                                 return;
163                             }
164 
165                             logEvent = "DEBUG: Response received from C1";
166                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
167                             
168                             // *** LOG TIME
169                             endTime = DateTime.Now;
170                             ts = endTime.Subtract(startTime);
171                             elapsedTimeMS = ts.Milliseconds;
172                             elapsedTimeSeconds = ts.Seconds;
173                             logEvent = "DEBUG: Document response was received from ConfigureOne in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
174                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
175 
176                             XmlNodeList xnldoc = xmlResult.GetElementsByTagName("content");
177                             foreach (XmlNode node in xnldoc)
178                             {
179                                 logEvent = "DEBUG: Copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex];
180                                 if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
181 
182                                 //Format Sharepoint directory, create if not exist, and copy document to that location
183                                 string copyToLocation = DatabaseFactory.COItemitem(C1WebService.SPOrderNumber, documentSerialNumber);
184 
185                                 //Create directory if not exists
186                                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation + copyToLocation); }
187                                 catch (Exception edi)
188                                 {
189                                     //directory already exists, nothing further needs done
190                                 }
191 
192                                 logEvent = "SharepointCopyLocation Value: " + SharepointCopyLocation;
193                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
194 
195                                 logEvent = "CopyToLocation Value: " + copyToLocation;
196                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
197 
198                                 logEvent = "Full-Path Value: " + SharepointCopyLocation + copyToLocation;
199                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
200 
201                                 logEvent = "Full-Path with file Value: " + SharepointCopyLocation + copyToLocation + "\\" + docs[arrayindex];
202                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
203 
204                                 startTime = DateTime.Now;
205 
206                                 try
207                                 {
208                                     byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
209                                     //File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
210                                     File.WriteAllBytes(SharepointCopyLocation + copyToLocation + "\\" + docs[arrayindex], pdfByteArray);
211                                     documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
212 
213                                     // *** LOG TIME
214                                     endTime = DateTime.Now;
215                                     ts = endTime.Subtract(startTime);
216                                     elapsedTimeMS = ts.Milliseconds;
217                                     elapsedTimeSeconds = ts.Seconds;
218                                     logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
219                                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
220                                 }
221                                 catch (Exception ts1)
222                                 {
223                                     logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS1 Exception.  A maximum of 5 retries will be attempted.";
224                                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
225                                     bool successfulDocument = false;
226                                     for (int i = 0; i < 5; i += 1)
227                                     {
228                                         if (successfulDocument) { break; }
229                                         try
230                                         {
231                                             byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
232                                             File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
233                                             documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
234 
235                                             // *** LOG TIME
236                                             endTime = DateTime.Now;
237                                             ts = endTime.Subtract(startTime);
238                                             elapsedTimeMS = ts.Milliseconds;
239                                             elapsedTimeSeconds = ts.Seconds;
240                                             logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
241                                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
242                                             successfulDocument = true;
243                                         }
244                                         catch (Exception rt1)
245                                         {
246                                             logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "rt1 Exception on Retry#: " + (i + 1).ToString();
247                                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
248                                         }
249                                     }
250 
251                                     if (!successfulDocument)
252                                     {
253                                         // *** LOG TIME
254                                         endTime = DateTime.Now;
255                                         ts = endTime.Subtract(startTime);
256                                         elapsedTimeMS = ts.Milliseconds;
257                                         elapsedTimeSeconds = ts.Seconds;
258                                         logEvent = "DEBUG: Time that elapsed before timeout occurred for current document: " + Convert.ToString(elapsedTimeMS) + "ms, " + Convert.ToString(elapsedTimeSeconds) + " s";
259                                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
260                                         //SendMail.MailMessage(logEvent, "TIMEOUT");
261                                     }
262                                 }
263                             }
264                         }
265                     }
266                     catch (Exception ts2)
267                     {
268                         logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS2 Exception";
269                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
270                         SendMail.MailMessage(logEvent, "TIMEOUT");
271                     }
272                     arrayindex += 1;
273                 }
274                 
275                 //Save XML output to SharePoint as well
276                 File.Copy(C1WebService.SPPUBOrderNumber + "_XMLOutput.txt", SharepointCopyLocation + C1WebService.SPPUBOrderNumber + "_XMLOutput.txt", true);
277 
278                 logEvent = "Retrieved/Saved Following Document Files: " + Environment.NewLine + Environment.NewLine + documentFilesSaved;
279                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
280 
281                 //Update order-status and ref number in C1 to Ordered and SL order#, respectively
282                 key = "updateOrder";
283                 useMethod = C1Dictionaries.webmethods[key];
284                 string C1orderNumber = orderNumber;
285                 string C1orderReference = C1WebService.SPOrderNumber;
286                 string C1orderStatus = "OR";              //Ordered
287                 xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soap:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:soap=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soap:Body><" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><param1>" + orderNumber + "</param1><param2>" + C1orderReference + "</param2><param3>" + C1orderStatus + "</param3></" + key + "></soap:Body></soap:Envelope>";
288                 sURL = useMethod;
289                 C1WebService.pubURL = sURL;
290                 C1WebService.pubPayload = xmlPayload;
291                 logEvent = "XML Payload To AXIS: " + xmlPayload;
292                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
293                 objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
294                 objRequest.Method = "POST";
295                 objRequest.ContentType = "text/xml";
296                 objRequest.Headers.Add("SOAPAction", key);
297                 objRequest.Timeout = 120000;
298                 objRequest.ReadWriteTimeout = 120000;
299 
300                 data = new StringBuilder();
301                 data.Append(xmlPayload);
302                 byte[] byteDataStatus = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
303                 objRequest.ContentLength = byteDataStatus.Length;
304                 using (Stream postStream = objRequest.GetRequestStream())
305                 {
306                     postStream.ReadTimeout = 120000;
307                     postStream.WriteTimeout = 120000;
308                     postStream.Write(byteDataStatus, 0, byteDataStatus.Length);
309                 }
310 
311                 startTime = DateTime.Now;
312                 
313                 //return response from AXIS (if any)
314                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
315                 {
316                     StreamReader reader = new StreamReader(response.GetResponseStream());
317                     result = reader.ReadToEnd();
318                     reader.Close();
319                     response.Close();
320                 }
321 
322                 try
323                 {
324                     xmlResult.LoadXml(result);
325                     xnl = xmlResult.GetElementsByTagName("success");
326                     foreach (XmlNode node in xnl)
327                     {
328                         switch (node.InnerText)
329                         {
330                             case "true":
331                                 logEvent = "Status-Set Result: Success";
332                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
333                                 break;
334                             default:
335                                 logEvent = "Status-Set Result: Failure";
336                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
337                                 break;
338                         }
339                     }
340                 }
341                 catch (Exception ex2)
342                 {
343                     logEvent = "ERROR LOADING XML FROM WEB SERVICE ON C1 ORDER STATUS SET: " + ex2.Message;
344                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
345                     return;
346                 }
347 
348                 logEvent = "C1 Order Status And Reference Completed For C1 Order: " + orderNumber + " - Syteline Order: " + C1WebService.SPOrderNumber;
349                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
350             }
351             catch (Exception exd)
352             {
353                 logEvent = "ERROR: " + exd.Message + " -> " + exd.Source;
354                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
355             }
356         }
357     }
